import Pagination from "@/components/Pagination"
import orgSelect from "@/components/Select/orgSelect.vue"
import { DICT_CODE, DT_ORG_TYPE } from "@/utils/constant"
import { mapState, mapGetters } from "vuex"
import warehouseSelect from "@/components/Select/warehouse-select.vue" //下拉组件
import wlxzbykcz from "@/components/Select/wlxzbykcz"

// import materielMmultipleTable from "@/components/table/materiel-multiple-table.vue";
import * as api from "@/api/project/wms/productReturn/returnInstruction"
import workCenterSelect from "@/components/Select/work-center-select.vue" //下拉组件
import AuthoType from "@/components/Select/AuthoType.vue" //权限类型下拉组件

export default {
    name: "returnInstruction",
    components: {        AuthoType,
        Pagination,
        orgSelect,
        warehouseSelect,
        wlxzbykcz,
        workCenterSelect    },
    data () {
        return {
            theight: 0,
            isSingle: true,//表格是否单选 点击各按钮根据流程逻辑控制单多选
            tableKey: [0, 1],
            list: null,
            list1: null,
            list2: null,
            total: 0,
            total1: 0,
            total2: 0,
            value1: "",
            tkq: "",
            value2: "",
            value3: "",
            listQuery: {
                page: true,
                currentPage: 1,
                pageSize: 10,
                orgCode: undefined,
                lineCode: undefined,
                productionline: undefined,
                station: undefined,
                feedType: undefined,
                state: undefined,
                batchNumber: undefined,
                leadTime: undefined,
                feedTime: undefined,
                feedwarehouse: undefined,
                workShop: undefined,
                method: 'instructions',
                dicType: undefined
            },
            listQuery1: {
                page: true,
                currentPage: 1,
                pageSize: 10,
                supplierCode: undefined,
                orderNumber: undefined
            },
            listQuery2: {
                page: true,
                currentPage: 1,
                pageSize: 10
            },
            listQueryItems: {
                currentPage: 1,
                pageSize: 10,
                pushOrderCode: undefined,
                dicType: undefined,
                method: 'instructions'
            },
            listQueryItemsb: {
                currentPage: 1,
                pageSize: 10,
                pushOrderCode: undefined,
                dicType: undefined,
            },
            dto: {
                goodsReadyEmp: undefined,
                goodsReadyEmpName: undefined,
                goodsReadyTime: undefined,
                lineCode: undefined,
                lineName: undefined,
                method: undefined,
                remark: undefined,
                orgCode: undefined,
                orgName: undefined,
                pushOrderCode: undefined,
                detailCreateDTOS: undefined,
                warehouseCode: undefined,
                workcenterWarehouse: undefined
            },
            dta: {
                method: undefined,
                instructionsNos: undefined
            },
            listLoading: true,
            isEaid: false,
            isEdit: true,
            parentsRows1: undefined,
            gw: undefined,
            feedArea: undefined,
            temp: {
                state: undefined,
                remark: undefined,
                rowNo: undefined,
                materielCode: undefined,
                materielName: undefined,
                orgCode: undefined,
                productionline: undefined,
                station: undefined,
                feedType: undefined,
                batchNumber: undefined,
                leadTime: undefined,
                feedTime: undefined,
                feedwarehouse: undefined,
                workShop: undefined,
                goodsReadyEmpName: undefined,
                materialputEmpName: undefined,
            },
            xzwlDialogFormVisible: undefined,
            blPersonnelDialogFormVisible1: undefined,
            tlPersonnelDialogFormVisible2: undefined,
            rules: {
                factory: [{ required: true, message: "请选择", trigger: "blur" }],
                pushMaterialType: [
                    { required: true, message: "请选择", trigger: "blur" }
                ],
                lineCode: [{ required: true, message: "请选择", trigger: "blur" }],
                workshopCode: [{ required: true, message: "请选择", trigger: "blur" }],
                warehouseCode: [{ required: true, message: "请选择", trigger: "blur" }],
                goodsReadyTime: [
                    { required: true, message: "请选择", trigger: "blur" }
                ],
            },
            // measureCode: undefined,
            selectCode: [],
            dialogFormVisible: false,
            dialogFormVisible1: false,
            employeeDialogFormVisible: false,
            dialogStatus: "",
            DICT_CODE: DICT_CODE,
            DT_ORG_TYPE: DT_ORG_TYPE,
            // dimensionOptions: DICT_CODE,
        }
    },
    computed: {
        ...mapGetters(["name", "superRoles"]),
        ...mapState({
            dt_pushmaterial_type: state => state.dict.dt_pushmaterial_type,
            dt_instruction_state: state => state.dict.dt_instruction_state,
            dt_return_type_screenGroupNum: state => state.businessComponent.dt_return_type_screenGroupNum
        })
    },
    mounted () {
        this.setTableHeight()
        //表格高度自适应
        window.onresize = () => {
            this.setTableHeight()
        }
        this.$store.dispatch("dict/getDicData", [
            "dt_pushmaterial_type",
            "dt_instruction_state",
            "dt_station_type"
        ])
        this.$store.dispatch("businessComponent/getBusinessComponentData", [
            "dt_return_type_screenGroupNum"
        ])
    },
    methods: {
        //表格高度计算
        setTableHeight () {
            this.theight = this.$myFun.getSingleTbHeight() - 195
        },
        getFeedArea (a) { //根据工位查投控区
            let b
            b = this.gw.filter(n => {
                return n.stationCode === a
            }).map(v => {
                return v.feedArea
            })
            this.tkq = b[0]
            return this.tkq
        },
        cxgw1 () { //查询工位
            api.queryRecordsgw(this.listQuery.lineCode).then(response => {
                response = response.data
                this.gw = response
            })
        },
        cxgw () { //查询工位
            api.queryRecordsgw(this.temp.lineCode).then(response => {
                response = response.data
                this.gw = response

            })
        },
        queryworkshop (v) { //查询车间仓库
            this.temp.workcenterWarehouse = v[0].feedArea
        },
        handleorg (val) {
            this.listQuery.orgCode = val[0].organizationCode
            this.getList()

        },
        getList () { //查询主表
            if (this.superRoles) {
                this.listQuery.creator = null
            } else {
                this.listQuery.creator = this.name
            }
            if (this.value1) {
                var d1 = this.value1[0]
                var d2 = this.value1[1]
                this.listQuery.returnDateBegin = d1
                this.listQuery.returnDateEnd = d2
            } else {
                this.listQuery.returnDateBegin = null
                this.listQuery.returnDateEnd = null
            }
            this.list1 = []
            api.queryRecords(this.listQuery).then(response => {
                response = response.data
                this.list = response.list
                this.total = response.pages.count
                this.$nextTick(function () {
                    this.checked()
                })

            })
        },
        checked () {
            this.$refs.tb.toggleRowSelection(this.list[0], true)//官网方法，重点
        },
        getItemsList () {  //查询订单明细
            this.listQueryItems.instructionsNo = this.selectlistRow.instructionsNo
            this.listQueryItems.dicType = this.selectlistRow.dicType
            this.listQueryItems.orgCode = this.listQuery.orgCode
            this.listQueryItems.warehouseCode = this.listQuery.warehouseCode
            api.queryRecorditems(this.listQueryItems).then(response => {
                response = response.data
                this.list1 = response.list
                this.total1 = response.pages.count
            })
        },
        selectPartRow (rows) { //父子通信
            this.parentsRows = rows
        },
        handleQuery () {
            if (this.listQuery.orgCode && this.listQuery.dicType) {
                this.listQuery.currentPage = 1
                this.getList()
            } else {
                this.$message({
                    title: "警告",
                    message: "请选择组织，生产线，业务类型",
                    type: "warning",
                    duration: 2000
                })
            }
        },
        handleCreate () { //新增
            this.resetTemp()
            this.dialogStatus = "create"
            this.cxgw()
            this.dialogFormVisible = true
            this.list2 = []
        },
        orgname (name) {
            this.dto.orgName = name
        },
        linename (name) {
            this.dto.lineName = name
        },
        create () { //新增
            this.dto = this.temp
            this.dto.detailCreateDTOS = this.list2
            api.addRecord(this.dto).then(() => {
                this.getList()
                this.dialogFormVisible = false
                this.$message({
                    title: "成功",
                    message: "新增成功",
                    type: "success",
                    duration: 2000
                })
                this.list2 = []
            })
        },

        handleDelete () { //删除
            if (this.selectlistRow) {
                this.$confirm("此操作将永久删除该文件,是否继续?", "提示", {
                    confirmButtonText: "确定",
                    cancelButtonText: "取消",
                    type: "warning"
                }).then(() => {
                    let ids = []
                    let a = []
                    a.push(this.selectlistRow)
                    a.forEach((val, index) => {
                        ids[index] = val.instructionsNo
                    })
                    api.deleteRecord(ids).then(() => {
                        this.getList()
                        this.$message({
                            title: "成功",
                            message: "删除成功",
                            type: "success",
                            duration: 2000
                        })
                    })
                }).catch(() => {
                    this.$message({
                        type: "info",
                        message: "已取消删除"
                    })
                })

            } else {
                this.$message({
                    title: "警告",
                    message: "请选择一条信息",
                    type: "warning",
                    duration: 2000
                })
            }
        },
        handleCreatewl () {//新增物料
            if (this.temp.orgCode && this.temp.lineCode) {
                this.xzwlDialogFormVisible = !this.xzwlDialogFormVisible
                this.$nextTick(() => {
                    this.$refs.wlxzRef.listSelected = [...this.list2]
                    this.$refs.wlxzRef.getList()
                })
            } else {
                this.$message({
                    title: "警告",
                    message: "请先选择组织和生产线",
                    type: "warning",
                    duration: 2000
                })
            }
        },
        materialxz () { //物料选中中弹窗确认按钮
            this.parentsRows = this.$refs.wlxzRef.listSelected
            if (this.parentsRows) {
                this.list2 = this.parentsRows
                this.xzwlDialogFormVisible = false
            } else {
                this.$message({
                    title: this.$t("message.warning"),
                    message: this.$t("message.checkedplease"),
                    type: "warning"
                })
            }
        },
        setDetails (data) {
            data.forEach((item) => {
                this.list2.push(item)
            })
        },
        handleDeleteitems () {//删除物料
            if (this.selectlistRow4.length !== 0) {
                this.$confirm("确认要删除该数据项吗?", "提示", {
                    confirmButtonText: "确定",
                    cancelButtonText: "取消",
                    type: "warning"
                }).then(() => {
                    this.list2 = this.list2.filter(item => {
                        let isD = true
                        this.selectlistRow4.forEach((itemr) => {
                            if (itemr.materielCode === item.materielCode) {
                                isD = false
                            }
                        })
                        return isD
                    })
                    this.$message({
                        type: "success",
                        message: "删除成功"
                    })
                }).catch(() => {
                    this.$message({
                        type: "info",
                        message: "已取消"
                    })
                })
            } else {
                this.$message.warning("请勾选记录！")
                return
            }
        },
        handleUpdate () {//编辑 
            this.resetTemp()
            if (this.selectlistRow) {
                if (this.selectlistRow.state !== 1) {
                    this.temp = this.selectlistRow
                    this.listQueryItemsb.instructionsNo = this.selectlistRow.instructionsNo
                    this.listQueryItemsb.dicType = this.selectlistRow.dicType
                    this.listQueryItemsb.orgCode = this.selectlistRow.orgCode
                    this.listQueryItemsb.warehouseCode = this.selectlistRow.warehouseCode
                    api.queryRecorditems(this.listQueryItemsb).then(response => {
                        this.list2 = response.data.list
                        this.total2 = response.data.pages.count
                    })
                    this.cxgw()
                    this.dialogFormVisible = true
                    this.dialogStatus = "update"
                } else {
                    this.$message({
                        title: "警告",
                        message: "发布状态下不可编辑",
                        type: "warning",
                        duration: 2000
                    })
                }
            } else {
                this.$message({
                    title: "警告",
                    message: "请选择一条信息",
                    type: "warning",
                    duration: 2000
                })
            }
        },

        update () {//编辑
            this.temp.detailCreateDTOS = this.list2
            api.updateRecord(this.selectlistRow.instructionsNo, this.temp).then(() => {
                this.dialogFormVisible = false
                this.$message({
                    title: "成功",
                    message: "编辑成功",
                    type: "success",
                    duration: 2000
                })
                this.getList()
                this.getItemsList()
            })
        },
        handleDblclickgysxz (row) { //父子页面通信
            this.parentsRows = row
            // this.gysxzDialogFormVisible = false;
        },
        handlezf () {//关闭
            if (this.selectlistRow) {
                let ids = []
                let a = []
                a.push(this.selectlistRow)
                a.forEach((val, index) => {
                    ids[index] = val.instructionsNo
                })
                this.dta.instructionsNos = ids
                this.dta.method = 'close'
                api.updateRecordclose(this.dta).then(() => {
                    this.$message({
                        title: "成功",
                        message: "作废成功",
                        type: "success",
                        duration: 2000
                    })
                    this.getList()
                })
            } else {
                this.$message({
                    title: "警告",
                    message: "请选择一条信息",
                    type: "warning",
                    duration: 2000
                })
            }
        },
        handlePublish () {//发布
            if (this.selectlistRow) {
                let ids = []
                let a = []
                a.push(this.selectlistRow)
                a.forEach((val, index) => {
                    ids[index] = val.instructionsNo
                })
                this.dta.instructionsNos = ids
                this.dta.method = 'publish'
                api.updateRecordclose(this.dta).then(() => {

                    this.$message({
                        title: "成功",
                        message: "发布成功",
                        type: "success",
                        duration: 2000
                    })
                    this.getList()
                })
            } else {
                this.$message({
                    title: "警告",
                    message: "请选择一条信息",
                    type: "warning",
                    duration: 2000
                })
            }
        },
        update1 () {//供应商选中弹窗确认按钮
            if (this.parentsRows.length && this.parentsRows.length == 1) {
                this.temp.supplierCode = this.parentsRows[0].supplierCode
                this.temp.supplierName = this.parentsRows[0].supplierName
                this.xzwlDialogFormVisible = false
            } else {
                this.$message({
                    title: "警告",
                    message: "请选择一条信息",
                    type: "warning"
                })
            }
        },

        handleDeleteBatch () {
            if (this.selectCode.length == 0) {
                this.$message.warning("请勾选记录！")
                return
            }
            this.delete(this.selectCode)
        },
        selectRow (val) {//表一复选框选中方法
            this.selectlistRow = val[val.length - 1]
            if (val.length > 1) {
                this.$refs.tb.clearSelection()
                this.$refs.tb.toggleRowSelection(val.pop())
            }
            this.$refs.tb.setCurrentRow(this.selectlistRow)
            if (this.selectlistRow) {
                this.getItemsList()
            }
        },
        selectRow1 (val) { //表二复选框选中方法
            this.selectlistRow1 = val
        },
        selectRow2 (val) {//表一单击行选中方法
            this.selectlistRow2 = val
            this.$refs.tb.clearSelection()
            this.$refs.tb.toggleRowSelection(val)
        },

        selectRow3 (val) {//表二单击行选中方法
            this.selectlistRow3 = val
            // this.$refs.tb.clearSelection()
            // this.$refs.tb.toggleRowSelection(val)//单击行绑定点击事件
        },
        selectRow4 (val) {
            this.selectlistRow4 = val
        },
        setdefault (val, i) {
            if (i === 1) {
                this.listQuery.orgCode = val
            } else if (i === 3) {
                this.listQuery.warehouseCode = val
                this.listQuery.dicType = this.dt_return_type_screenGroupNum.options[0].wareRuleCode
                this.getList()
            }
        },
        cancel () {
            this.dialogFormVisible = false
        },
        resetTemp () {
            this.temp = {
                orgCode: undefined,
                pushMaterialType: undefined,
                lineCode: undefined,
                goodsReadyTime: undefined,
                pushTime: undefined,
                warehouseCode: undefined,
                workshopCode: undefined,
                goodsReadyEmpName: undefined,
                pushEmpName: undefined,
                note: undefined
            }
        },
        handleClose () {
            this.resetTemp()
            this.$refs.temp.resetFields()
        },
        handleSelectionChange (selection) {
            this.selectCode = selection.map(v => v.measureCode)
        }
    }
};
