@import '~@/styles/iot.scss';
.el-row {
    position: relative;
}
.flot {
    position: absolute;
    top: 0;
    z-index: 9;
}
.flot.left {
    left: 0;
}
.flot.right {
    right: 0;
}
.bs-1 {
    background: url(./one.png) no-repeat;
}
.bs-2,
.bs-3 {
    background: url(./two.png) no-repeat;
}
.bs-1,
.bs-2,
.bs-3 {
    background-size: 100% 100%;
    margin: 5px;
    display: flex;
    flex-direction: column;
}

.carousel-box {
    overflow: hidden;
}

.amain {
    transition: all 2s linear;
}

.charts-bgc {
    background: url(./BGI.png) 100% 100%;
}

.el-row,
.el-col {
    height: 100%;
}

.el-col {
    // position: relative;
    display: flex;
    flex-direction: column;
    justify-content: space-between;
}

.bs-1,
.center-1 {
    flex: 2;
}

.bs-2 {
    flex: 3;
    overflow: hidden;
}
.bs-3 {
    flex: 3;
    overflow: hidden;
}

.center-2 {
    flex: 7;
}

.center-1 {
    position: absolute;
    top: 5px;
    left: 50%;
    transform: translateX(-50%);
    width: 50%;
    display: flex;
    justify-content: space-around;
}

.center-1 .info {
    padding: 10px 0;
    width: 22%;
    display: flex;
    flex-direction: column;
    justify-content: space-around;
    align-items: center;
    background: url(./topBgi.png) no-repeat;
    background-size: 100% 100%;
}

.center-1 .info .title {
    font-weight: 500;
    font-size: 14px;
    color: #499be8;
}

.center-1 .info .number {
    font-weight: 500;
    font-size: 28px;
    color: #fff;
}

.bs-1 .title,
.bs-2 .title,
.bs-3 .title {
    display: flex;
    justify-content: space-between;
    padding: 15px 10px 2px;
    font-size: 12px;
    font-weight: 600;
    color: #fff;
}

.carousel-item {
    padding: 0;
    font-size: 14px;
    margin: 0;
}

.carousel-item li {
    padding: 10px;
    list-style: none;
}

.carousel-item li .info {
    color: #fff;
}
.medium {
    display: flex;
    justify-content: flex-start;
    font-size: 12px;
}
.medium .comp {
    // margin-left: 10%;
    text-align: right;
    width: 30%;
    padding-right: 10px;
    color: #fff;
}
.medium .warning {
    color: #d4380d;
}

.medium .success {
    color: #67c23a;
}

.nav {
    display: flex;
    justify-content: flex-end;
    width: 50%;
    position: absolute;
    left: 50%;
    transform: translateX(-50%);
    bottom: 10px;
    padding: 10px;
    z-index: 9;
    span {
        padding: 5px;
        color: #22b6ed;
        cursor: pointer;
        border: 1px solid #22b6ed;
        &:hover {
            color: #fff;
        }
    }
}
