import { DICT_CODE } from '@/utils/constant'
import caiGou from '@/components/Select/xtpt/caigou'//下拉组件
import * as api from "@/api/project/srm/contractManage/problemManagement"
import dialogOrgSelect from '@/components/Dialog/dialogOrgSelect'
import dialogPersonSelect from '@/components/Dialog/dialogPersonSelect.vue'//选择人
import dialogGysxz from '@/components/Dialog/dialogsGysxz'//供应商选择
import dialogMaterielCategory from '@/components/Dialog/dialogMaterielCategory.vue'//物料分类
import dialogWlxzbyzsj from '@/components/Dialog/dialogMaterial.vue'//主数据物料选择
import { mapState } from "vuex"
export default {
    components: { caiGou, dialogOrgSelect, dialogPersonSelect, dialogGysxz, dialogMaterielCategory, dialogWlxzbyzsj },
    data () {
        return {
            status: false,
            statestatus: false,
            DICT_CODE: DICT_CODE,
            enableds: false,
            dialogStatus: '',
            dialogVisible: false,
            dialogwlVisible: false,
            treeData: [],
            listQuery: {},
            temp: {
                rowId: undefined,
                questionNo: undefined,
                orgCode: undefined,
                submmitDepartmentName: undefined,
                submitter: undefined,
                submmitTime: undefined,
                firstFindTime: undefined,
                supplierCode: undefined,
                materielClass: undefined,
                materielCode: undefined,
                questionDesc: undefined,
                causeConsequence: undefined,
                severityLevel: undefined,
                statisticalBigClass: undefined,
                state: undefined,
                costLose: undefined,
                workhoursLose: undefined,
                enableds: undefined,
                statisticalMidClass: undefined,
                resendNum: undefined,
                resendTime: undefined,
                statisticalSmallClass: undefined,
                handleOrderNo: undefined,
                handler: undefined,
                handleTypeCode: undefined,
                predictCompletionTime: undefined,
                reasonAnalysis: undefined,
                solutions: undefined,
                handleStateCode: 'PCJ4',
                handleFinishTime: undefined,
                closeTime: undefined,
                realSolveTime: undefined,
                solveResult: undefined
            },
            titlePerson: null,
        }
    },
    computed: {
        ...mapState({
            dt_srm_pdn: state => state.dict.dt_srm_pdn,
            dt_srm_pc5: state => state.dict.dt_srm_pc5,
            dt_srm_pc4: state => state.dict.dt_srm_pc4,
            dt_srm_pc3: state => state.dict.dt_srm_pc3,
            dt_srm_pcd: state => state.dict.dt_srm_pcd,
            dt_srm_pb4: state => state.dict.dt_srm_pb4,
            dt_srm_pcj: state => state.dict.dt_srm_pcj,
        })
    },
    mounted () {
        this.$store.dispatch("dict/getDicData", ['dt_srm_pdn', 'dt_srm_pc5', 'dt_srm_pc4', 'dt_srm_pc3', 'dt_srm_pcd', 'dt_srm_pb4', 'dt_srm_pcj'])
    },
    methods: {
        initForm (val, row) {
            console.log(row, 'kkkk')
            this.$refs['dataForm'].resetFields()
            this.$refs['dataForm'].clearValidate()
            this.dialogStatus = val
            this.temp = { ...row }
            if (val == 'create') {
                this.temp.handleStateCode = 'PCJ4'
                this.$set(this.temp, 'handleFinishTime', this.timeFormat())
                this.status = false
                this.statestatus = false
            }
            if (val == 'update') {
                this.statestatus = true
            }
            if (val == 'search') {
                this.statestatus = true
                this.status = true

            } else {
                this.status = false
            }
            if (row.state == 'PC31') {
                this.temp.enableds = true
            } else {
                this.temp.enableds = false
            }
        },
        timeFormat () {

            let yy = new Date().getFullYear()
            let mm = new Date().getMonth() + 1
            let dd = new Date().getDate()

            let hh = new Date().getHours()
            let mf =
                new Date().getMinutes() < 10
                    ? "0" + new Date().getMinutes()
                    : new Date().getMinutes()
            let ss =
                new Date().getSeconds() < 10
                    ? "0" + new Date().getSeconds()
                    : new Date().getSeconds()
            let clock = yy + "-" + mm + "-" + dd + " " + hh + ":" + mf + ":" + ss
            return (clock)
        },
        //申请单位
        showOrg () {
            this.$refs.refOrg.dialogVisible = true
        },
        //申请单位确定
        confirmOrg (data) {
            console.log(data)
            if (data) {
                this.$set(this.temp, 'submmitDepartmentCode', data[0].organizationCode)
                this.$set(this.temp, 'submmitDepartmentName', data[0].organizationName)
            } else {
                this.$set(this.temp, 'submmitDepartmentCode', undefined)
                this.$set(this.temp, 'submmitDepartmentName', undefined)
            }
        },
        //人员选择
        showPerson (val) {
            console.log(val, 'person')
            if (val == 'TJperson') {
                this.titlePerson = '提交人'
            } else if (val == 'QRperson') {
                this.titlePerson = '确认人'
            } else {
                this.titlePerson = '解决负责人'
            }
            this.$refs.person.dialogVisible = true
        },
        //人员选择确定
        personConfirm (val) {
            if (this.titlePerson == '提交人') {
                if (val.length > 0) {
                    this.$set(this.temp, 'submmitCode', val[0].employeeCode)
                    this.$set(this.temp, 'submitter', val[0].employeeName)
                } else {
                    this.$set(this.temp, 'submmitCode', undefined)
                    this.$set(this.temp, 'submitter', undefined)
                }
            }
            if (this.titlePerson == '确认人') {
                if (val.length > 0) {
                    this.$set(this.temp, 'confirmCode', val[0].employeeCode)
                    this.$set(this.temp, 'confirmer', val[0].employeeName)
                } else {
                    this.$set(this.temp, 'confirmCode', undefined)
                    this.$set(this.temp, 'confirmer', undefined)
                }
            }
            if (this.titlePerson == '解决负责人') {
                if (val.length > 0) {
                    this.$set(this.temp, 'handlerCode', val[0].employeeCode)
                    this.$set(this.temp, 'handler', val[0].employeeName)
                } else {
                    this.$set(this.temp, 'handlerCode', undefined)
                    this.$set(this.temp, 'handler', undefined)
                }
            }
        },
        showGys () {
            this.$refs.refGys.dialogVisible = true
            this.$refs.refGys.getList()
        },
        //供应商选择确定
        handleGysClick (val) {
            console.log(val)
            this.$set(this.listQuery, 'supplierName', val[0].supplierName)
            this.$set(this.listQuery, 'supplierCode', val[0].supplierCode)
        },
        //物料分类
        handleClassifyCode () {
            if (this.temp.orgCode) {
                this.$refs.wlfl.listQuery.orgCode = this.temp.orgCode
                this.$refs.wlfl.selectedRows = []
                this.$refs.wlfl.handleQuery()
                this.$refs.wlfl.dialogVisible = true
            } else {
                this.$message({ type: 'warning', message: '请先选择采购组织' })
            }
        },
        //物料分类选择确定
        confirmClassify (data) {
            this.$set(this.temp, 'materielClass', data[0].classCode)
            this.$set(this.temp, 'materielClassName', data[0].className)
        },
        //物料选择
        handleMaterialCode () {
            this.$refs.refZsjWlxz.dialogVisible = true
        },
        //物料选择确定
        confirmMateriel (data) {
            console.log(data, 'bbbvvv')
            this.$set(this.temp, 'materielCode', data[0].materielCode)
            this.$set(this.temp, 'materielName', data[0].materielName)
        },
        handleSave () {
            api.addRecordItems(this.temp).then(() => {
                this.$message({
                    title: '成功',
                    message: '新增成功',
                    type: 'success',
                    duration: 2000
                })
                this.$emit('changeState', 'problemManage')
                this.$emit('refresh')
            })
        },
        updateData () {
            api.updateRecordItems(this.temp.rowId, this.temp).then(() => {
                this.$message({
                    title: '成功',
                    message: '编辑成功',
                    type: 'success',
                    duration: 2000
                })
                this.$emit('changeState', 'problemManage')
                this.$emit('refresh')
            })
        },
        handleClose () {
            this.$emit('changeState', 'problemManage')
        }
    }
}
