export default {
    name: 'pie',
    props: {
        chartData: {
            type: Object,
            default: undefined
        },
        name: {
            type: String,
            default: ''
        },
        xAxis: {
            type: Array,
            default: undefined
        },
        legend: {
            type: Array,
            default: undefined
        }
    },
    data () {
        return {
            myChart: null
        }
    },
    watch: {
        'chartData.data': {
            handler () {
                this.down()
            },
            deep: true,
            immediate: true
        }
    },
    methods: {
        down () {
            let opt = {
                title: { text: this.name },
                tooltip: {
                    confine: true,
                    trigger: 'item',
                    formatter: '{b} : {c} ({d}%)'
                },
                legend: {
                    left: 'center',
                    top: '6%',
                    data: this.legend
                },
                series: [
                    {
                        type: 'pie',
                        radius: '60%',
                        center: ['50%', '55%'],
                        selectedMode: 'single',
                        itemStyle: {
                            normal: {
                                color: function (params) {
                                    //自定义颜色
                                    var color = [
                                        '#fd6361',
                                        '#02b7ae',
                                        '#606b6d',
                                        '#05b7ab',
                                        '#fd6361',
                                        '#ff9669',
                                        '#37454e'
                                    ]
                                    return color[params.dataIndex]
                                }
                            }
                        },
                        data: this.chartData.data
                    }
                ]
            }
            this.$nextTick(() => {
                this.myChart = this.$echarts.init(this.$refs[this.chartData.id])
                this.myChart.setOption(opt, true)
            })
        }
    }
}
