import *  as api from '@/api/project/eas/el/equipmentSpotCheck'
import Pagination from '@/components/Pagination'
import deviceTypeSelect from '../../components/deviceTypeSelect'
import { mapState } from "vuex"

export default {
    name: 'equipmentSpotCheck',
    components: { Pagination, deviceTypeSelect },
    data () {
        return {
            list: [],
            options: [],
            total: 0,
            theight: 0, //表格高度
            listQuery: {
                page: true,
                currentPage: 1,
                pageSize: 15,
            },
            tableKey: [0],
        }
    },
    created () {
        this.getList()
    },
    computed: {
        // 设备类别字典
        ...mapState({
            dt_device_state: state => state.eamDict.dt_device_state,
            dt_device_category: state => state.eamDict.dt_device_category
        })
    },
    mounted () {
        this.setTableHeight()
        let p = new Promise((resolve) => {
            resolve(
                this.$store.dispatch("eamDict/getDicData", [
                    'dt_device_state', 'dt_device_category'
                ])
            )
        })
        Promise.all([p]).then(() => {
            this.getList()
        })
        //表格高度自适应
        window.onresize = () => {
            this.setTableHeight()
        }
        this.getList()
    },
    methods: {
        //查主表
        getList () {
            api.queryMainlist(this.listQuery).then(res => {
                this.list = res.data.list
                let options = [this.dt_device_state, this.dt_device_category]
                this.list = this.$myFun.srmCodeToName(
                    this.list,
                    options,
                    ['state', 'category']
                )
                this.total = res.data.pages.count
            })
        },
        getValue (value) {
            this.listQuery.typeCode = value.typeCode;
        },
        // 获取表格选中时的数据
        selectRow (val) {
            this.selectlistRow = val[val.length - 1]
            if (val.length > 1) {
                this.$refs.mainList.clearSelection();
                this.$refs.mainList.toggleRowSelection(val.pop());
            }
            this.$refs.mainList.setCurrentRow(this.selectlistRow);
            this.$emit("change", this.selectlistRow);

        },
        //主表单行点击方法
        selectRow_line (val) {
            this.$refs.mainList.clearSelection();
            this.$refs.mainList.toggleRowSelection(val);
        },
        //表格高度计算
        setTableHeight () {
            this.theight = this.$myFun.getSingleTbHeight() - 160
        },
        handleQuery () {
            //查询
            this.listQuery.currentPage = 1
            this.getList()
        },
    }
}
