import { DICT_CODE } from '@/utils/constant'
import dialogGysxz from '@/components/Dialog/dialogsGysxz'//供应商选择
import { mapState } from "vuex"
import dialogWlxzbyzsj from '@/components/Dialog/dialogMaterial.vue'//主数据物料选择
export default {
    components: { dialogGysxz, dialogWlxzbyzsj },
    data () {
        return {
            DICT_CODE: DICT_CODE,
            dialogVisible: false,
            dialogStatus: '',
            tableKey: 0,
            list: [],
            listYcl: [],//原材料
            listYgj: [],//外购件
            listZygz: [],//专用工装
            listJgf: [],//加工费
            listBzf: [],//包装费
            listWlf: [],//物流费
            temp: {},
            status: {
                ycl: true,//原材料
                ygj: true,//外购件
                zygz: true,//专用工装
                jgf: true,//加工费
                bzf: true,//包装费
                wlf: true,//物流费
            }
        }
    },
    computed: {
        ...mapState({
            dt_srm_pc2: state => state.dict.dt_srm_pc2,
            dt_srm_pdt: state => state.dict.dt_srm_pdt,
            dt_srm_pdl: state => state.dict.dt_srm_pdl,
            dt_srm_pdm: state => state.dict.dt_srm_pdm,
        })
    },
    mounted () {
        this.$store.dispatch("dict/getDicData", ["dt_srm_pc2", 'dt_srm_pdt', 'dt_srm_pdl', 'dt_srm_pdm'])
        this.getList()
    },
    methods: {
        initData (val) {
            let options = [this.dt_srm_pdl]
            val[0].actuarialPackingDTOS = this.$myFun.srmCodeToName(val[0].actuarialPackingDTOS, options, ['packingType'])
            let optionsWL = [this.dt_srm_pdm]
            val[0].actuarialLogisticsDTOS = this.$myFun.srmCodeToName(val[0].actuarialLogisticsDTOS, optionsWL, ['transportType'])
            this.temp = val[0]
            this.list = val[0].totalQuotations //报价表
            this.listYcl = val[0].actuarialRawMaterialDTOS //原材料
            this.listYgj = val[0].actuarialOutsourcingDTOS //外购件
            this.listZygz = val[0].actuarialToolingDTOS //工装
            this.listJgf = val[0].actuarialProcessingDTOS //加工
            this.listBzf = val[0].actuarialPackingDTOS //包装
            this.listWlf = val[0].actuarialLogisticsDTOS //物流
        },
        initForm (val) {
            let options = [this.dt_srm_pdl]
            val.actuarialPackingDTOS = this.$myFun.srmCodeToName(val.actuarialPackingDTOS, options, ['packingType'])
            let optionsWL = [this.dt_srm_pdm]
            val.actuarialLogisticsDTOS = this.$myFun.srmCodeToName(val.actuarialLogisticsDTOS, optionsWL, ['transportType'])
            this.temp = val
            this.list = val.totalQuotations //报价表
            this.listYcl = val.actuarialRawMaterialDTOS //原材料
            this.listYgj = val.actuarialOutsourcingDTOS //外购件
            this.listZygz = val.actuarialToolingDTOS //工装
            this.listJgf = val.actuarialProcessingDTOS //加工
            this.listBzf = val.actuarialPackingDTOS //包装
            this.listWlf = val.actuarialLogisticsDTOS //物流
        },
        //获取采购组织名称
        selectName (val) {
            this.temp.purchaseOrgName = val
        },
        //物料选择
        handleMaterialCode () {
            this.$refs.refZsjWlxz.dialogVisible = true
        },
        //物料选择确定
        confirmMateriel (data) {
            this.$set(this.temp, 'materialCode', data[0].materielCode)
            this.$set(this.temp, 'materialName', data[0].materielName)
            this.$set(this.temp, 'materielEngName', data[0].materielEngName)//物料英文名
            this.$set(this.temp, 'materielType', data[0].materielType)//物料类型
            this.$set(this.temp, 'materielTypeName', data[0].materielTypeName)//物料类型名称
            this.$set(this.temp, 'materielGroup', data[0].materielGroup)//物料组
            this.$set(this.temp, 'materielGroupName', data[0].materielGroupName)//物料组名称
        },
        //供应商
        showGys () {
            this.$refs.refGys.dialogVisible = true
            this.$refs.refGys.getList()
        },
        //供应商确定
        handleGysClick (val) {
            this.$set(this.temp, 'supplierCode', val[0].supplierCode)
            this.$set(this.temp, 'supplierName', val[0].supplierName)
        },
        getList () {
            // this.list = [
            //     { params1: '1', params2: 'LL_X7-98880', params3: 'LL_X7-98880', params4: '01', params5: '', params6: '', params7: '', params8: '', params9: '', params10: '', }
            // ]
        },
        handleCreate () {
            this.$nextTick(() => {
                this.$refs.wlxz.listSelected = [...this.list]
                this.$refs.wlxz.getList()
            })
            this.$refs.wlxz.dialogVisible = true
        },
        //物料选择确定
        confirmWlxz (val) {
            this.$nextTick(() => {
                this.list = [...val]
                this.$refs.wlxz.dialogVisible = false
            })

        },
        handleDelete (index) {
            this.$confirm("确认要删除该数据项吗?", "提示", {
                confirmButtonText: "确定",
                cancelButtonText: "取消",
                type: "warning"
            }).then(() => {
                this.list.splice(index, 1)
                this.$message({
                    type: "success",
                    message: "删除成功"
                })
            }).catch(() => {
                this.$message({
                    type: "info",
                    message: "已取消"
                })
            })
        },
        createData () {

        },
        updateData () {

        }
    }
}
