import { queryRecords } from '@/api/project/iot/PM/gatewayProducts'
export default {
    name: 'gatewaySelect',
    props: {
        disabled: {
            type: Boolean,
            default: false
        },
        placeholder: {
            type: String,
            default: '请选择网关类型'
        },
        gatewayCode: {
            type: String,
            default: undefined
        }
    },
    watch: {
        gatewayCode (val) {
            this.itemKey = val
        }
    },
    data () {
        return {
            options: [],
            itemKey: this.gatewayCode
        }
    },
    created () {
        this.getOptions()
    },
    methods: {
        getOptions () {
            queryRecords().then(res => {
                this.options = res.data
                if (this.gatewayCode) {
                    this.itemKey = this.gatewayCode
                }
            })
        },
        changeSelect (val) {
            this.$emit('change', val)
            let name = ''
            let org = this.options
                .filter(v => v.gatewayCode == val)
                .find(value => value)
            if (org) {
                name = org.gatewayName
            }
            this.$emit('obj', org)
            this.$emit('selectName', name)
        },
        handleClear () {
            this.$emit('clear')
        }
    }
}
