import * as api from "@/api/project/srm/supplierManage/supplierAccess/platformInProposal"
import { DICT_CODE } from '@/utils/constant'
import caiGou from '@/components/Select/xtpt/caigou'//下拉组件
import { mapState } from "vuex"


// import * as api from "@/api/system/role"
import Pagination from "@/components/Pagination"
import dialogImport from '@/components/Dialog/dialogImport'
import dialogMaterielCategory from '@/components/Dialog/dialogMaterielCategory'//物料分类选择
import dialogAdd from './dialog/dialogAdd'
import upload from '@/components/Dialog/upload'//附件上传
import examine from "./dialog/examine" //方案审核

export default {
    name: "platformInProposal",
    components: { caiGou, Pagination, dialogAdd, dialogImport, dialogMaterielCategory, upload, examine },
    data () {
        return {
            functionCode: null,
            fileType: null,
            theight: 0,//表格高度
            isSingle: true,//表格是否单选 点击各按钮根据流程逻辑控制单多选
            dataList: [],
            itemList: [],//从表数据
            total: 0,
            totalItem: 0,
            listQuery: {
                //查询
                page: true,
                currentPage: 1,
                pageSize: 10,
                proposalNo: null,
                orgCode: null,
                materielClass: null,
                materielClassName: null,
                auditResult: null,
                platformSeries: null,
                position: null,
                supplierName: null,
                auditState: null,
                startDate: null,
                endDate: null
            },
            itemQuery: {
                page: true,
                currentPage: 1,
                pageSize: 10,
                proposalNo: null
            },
            rangeDate: null,
            DICT_CODE: DICT_CODE,
            currentSelectedRow: null,//当前选中的行
            selectedRows: [],//多选时选中的所有行
            tableKey: 0, //表格索引
            filterConfig: [
                { type: 'selectcaiGou', label: '采购组织', prop: 'orgCode', width: '', copmNum: 0, class: 'filter-item' },
                { type: 'commonInput', label: '提案号', prop: 'proposalNo', width: '', class: 'filter-item ml_5' },
                { type: 'inputBtnMC', label: '物料分类', prop: 'materielClassName', width: '', class: 'ml_5' },
                { type: 'dictSelect', dType: 'dt_srm_p0', label: '状态', prop: 'state', width: '', class: 'filter-item ml_5' },
                { type: 'dictSelect', dType: 'dt_srm_pag', label: '审核结论', prop: 'auditResult', width: '', class: 'filter-item ml_5' },
                { type: 'srmBisSelect', dType: 'dt_cate_platformSeries', label: '平台系列', prop: 'platformSeries', key: 'code', value: 'name', width: '', class: 'filter-item ml_5' },
                { type: 'dictSelect', dType: 'dt_srm_pad', label: '平台定位', prop: 'position', width: '', class: 'filter-item ml_5' },
                { type: 'commonInput', label: '供应商名称', prop: 'supplierName', width: '', class: 'filter-item' },
                { type: 'dictSelect', dType: 'dt_srm_paf', label: '审核状态', prop: 'auditState', width: '', class: 'filter-item ml_5' },
                { type: 'daterange', label: '审核时间段', prop: 'par9', width: '250px', startpl: '开始时间', endpl: '结束时间', class: 'filter-item ml_5' },
            ],
            oprateBtnConfig: [
                { id: 0, class: 'filter-item', icon: 'el-icon-plus', clickMethod: 'handleAdd', name: '提案录入' },
                { id: 1, class: 'filter-item ml_5', icon: 'el-icon-plus', clickMethod: 'handleImport', name: '附件' },
                { id: 2, class: 'filter-item ml_5', icon: 'el-icon-edit-outline', clickMethod: 'handlePublish', name: '发布' }
            ],
            tableConfig: [
                { label: '物料分类', prop: 'materielClass', align: 'center', width: 'auto' },
                { label: '物料分类名称', prop: 'materielClassName', align: 'center', width: 'auto' },
                { label: '平台系列', prop: 'platformSeriesCN', align: 'center', width: 'auto' },
                { label: '平台定位', prop: 'positionCN', align: 'center', width: 'auto' },
                { label: '提案号', prop: 'proposalNo', align: 'center', width: '140' },
                { label: '状态', prop: 'stateCN', align: 'center', width: 'auto' },
                { label: '提案备注', prop: 'remark', align: 'center', width: '180' },
                { label: '审核状态', prop: 'auditStateCN', align: 'center', width: 'auto' },
                { label: '审核结论', prop: 'auditResultCN', align: 'center', width: 'auto' },
                { label: '创建人', prop: 'creator', align: 'center', width: 'auto' },
                { label: '提案创建时间', prop: 'createTime', align: 'center', width: '140' },
                { label: '修改人', prop: 'updator', align: 'center', width: 'auto' },
                { label: '修改时间', prop: 'updateTime', align: 'center', width: '140' }
            ],
            tableConfigSub: [
                { label: '供应商编码', prop: 'supplierCode', align: 'center', width: '180' },
                { label: '供应商名称', prop: 'supplierName', align: 'center', width: '180' },
                { label: '注册地址', prop: 'registeredAddress', align: 'center', width: '180' },
                { label: '配套主机厂', prop: 'mainFactory', align: 'center', width: '180' },
                { label: '工厂地址', prop: 'contactAddress', align: 'center', width: '180' },
                { label: '建议理由', prop: 'remark', align: 'center', width: '180' },
                { label: '供应商定位', prop: 'positionCN', align: 'center', width: 'auto' },
                { label: '法人代表', prop: 'legalPerson', align: 'center', width: 'auto' },
                { label: '创建人', prop: 'creator', align: 'center', width: 'auto' },
                { label: '创建时间', prop: 'createTime', align: 'center', width: '140' },
                { label: '修改人', prop: 'updator', align: 'center', width: 'auto' },
                { label: '修改时间', prop: 'updateTime', align: 'center', width: '140' }
            ],
            dialogConfig: [
                { titleA: '入平台提案新增', title: '入平台提案编辑', curdialogStatus: 'add', unMutiple: false },
                { title: '上传附件' },
                { title: '发布' },
                { title: '选择物料分类', isRequired: false, unMutiple: true }
            ],
            modalNo: 'EXTP-BAS-0001',//导入模板编号,
            dialogImportVisible: false
        }
    },
    created () { },
    computed: {
        ...mapState({
            dt_srm_p0: state => state.dict.dt_srm_p0,//状态
            dt_srm_pag: state => state.dict.dt_srm_pag,//审核结论
            dt_cate_platformSeries: state => state.srmBisSelect.dt_cate_platformSeries,//平台系列
            dt_srm_pad: state => state.dict.dt_srm_pad,//平台定位
            dt_srm_paf: state => state.dict.dt_srm_paf//审核状态
        })
    },
    mounted () {
        this.$store.dispatch("dict/getDicData", ["dt_srm_p0", "dt_srm_pag", "dt_srm_pad", "dt_srm_paf"])
        this.$store.dispatch("srmBisSelect/getBisData", ["dt_cate_platformSeries"])

        this.setTableHeight()
        //表格高度自适应
        window.onresize = () => {
            this.setTableHeight()
        }
        this.getList()
    },
    watch: {
        'rangeDate' (value) {
            if (value) {
                this.listQuery.startDate = value[0]
                this.listQuery.endDate = value[1]
            }
        }
    },
    methods: {
        //表格高度计算
        setTableHeight () {
            this.theight = this.$myFun.getSingleTbHeight() - 100
        },
        refresh () {
            this.getList()
        },
        //获取表格数据
        getList () {
            api.queryRecords(this.listQuery).then(res => {
                let options = [this.dt_srm_p0, this.dt_srm_pag, this.dt_cate_platformSeries.data, this.dt_srm_pad, this.dt_srm_paf]
                res.data = this.$myFun.transCodeToName(res.data, options, [
                    'state', 'auditResult', ['platformSeries', 'code', 'name'], 'position', 'auditState'
                ])
                this.dataList = res.data.list
                this.total = res.data.pages.count
                this.setFirstLine()
            })
        },
        //单条数据获取
        getRow () {
            api.queryRow(this.currentSelectedRow.proposalNo).then(res => {
                let options = [this.dt_srm_pad]
                res.data.proposalItemDTOs = this.$myFun.codeToName(res.data.proposalItemDTOs, options, [
                    'position'
                ])
                this.itemList = res.data.proposalItemDTOs
            })
        },
        //从表详情获取
        // getItem () {
        //     this.itemQuery.proposalNo = this.currentSelectedRow.proposalNo
        //     api.queryItem(this.itemQuery).then(res => {
        //         this.itemList = res.data.list
        //         this.totalItem = res.data.pages.count
        //     })
        // },
        //表格查询
        handleQuery () {
            this.listQuery.currentPage = 1
            this.getList()
        },
        //表格显示条数变化
        handleSizeChange (val) {
            this.listQuery.pageSize = val
            this.getList()
        },
        //表格当前页变化
        handleCurrentChange (val) {
            this.listQuery.currentPage = val
            this.getList()
        },
        //审核按钮单击事件
        handleExamine () {
            if (this.currentSelectedRow) {
                this.$refs.examine.dialogVisible = true
                this.$refs.examine.temp.rowId = this.currentSelectedRow.rowId
            } else {
                this.$message({
                    title: '警告',
                    message: '请选择一条信息',
                    type: 'warning',
                    duration: 2000
                })
            }
        },
        //表格操作编辑
        handleUpdate (row) {
            api.queryRow(row.proposalNo).then(res => {
                this.dialogConfig[0].curdialogStatus = 'edit'
                this.$refs.refadd.dialogVisible = true
                this.$refs.refadd.isEdit = true
                this.$refs.refadd.initForm('edit', res.data)
            })
        },
        // 表格选择框选中 注:参数为选中的所有行的数组
        selectChange (val) {
            if (this.isSingle) {
                if (val.length > 1) {
                    this.$refs.tb.clearSelection() //清除其他行的选中
                    this.$refs.tb.toggleRowSelection(val[val.length - 1], "selected") //单击行绑定点击事件
                } else if (val.length === 1) {
                    this.selectedRows = val
                    this.currentSelectedRow = val[val.length - 1]
                    this.getRow()
                } else if (val.length === 0) {
                    this.selectedRows = val
                    this.currentSelectedRow = null
                    // this.itemList = []
                }
            } else {
                this.selectedRows = val
                this.currentSelectedRow = val[val.length - 1]
                this.getRow()
            }

        },
        //加载时默认选中第一行
        setFirstLine () {
            this.$nextTick(() => {
                if (this.dataList.length > 0) {
                    this.$refs.tb.toggleRowSelection(this.dataList[0], "selected")
                    this.$refs.tb.setCurrentRow(this.dataList[0])
                } else {
                    this.itemList = []
                }
            })
        },
        //点击表格某一行
        // rowClick (val) {
        //     this.$refs.tb.toggleRowSelection(val, "selected") //单击行绑定点击事件
        // },
        //确认添加/编辑
        confirmAddEdit (cdata) {
            if (this.dialogConfig[0].curdialogStatus === 'add') {
                //添加api post
                api.createRecords(cdata).then(() => {
                    this.$message({ type: 'success', message: '新增成功' })
                    this.getList()

                })
            } else if (this.dialogConfig[0].curdialogStatus === 'edit') {
                //编辑api patch
                api.updateRecords(cdata).then(() => {
                    this.$message({ type: 'success', message: '编辑成功' })
                    this.getList()
                })
            }
        },
        showMC () {
            if (!this.listQuery.orgCode) {
                this.$message({ type: 'warning', message: '请先选择组织编码' })
            } else {
                this.$refs.refMC.listQuery.orgCode = this.listQuery.orgCode
                this.$refs.refMC.handleQuery()
                this.$refs.refMC.dialogVisible = true
            }
        },
        handleConfirmMC (data) {
            this.listQuery.materielClass = data[0].classCode
            this.listQuery.materielClassName = data[0].className
        }
    }
};
