import * as api from '@/api/project/ems/dataBase/energyType'
import Pagination from '@/components/Pagination'
import { mapState } from 'vuex'
import throttle from 'lodash/throttle'

export default {
    name: 'workCenter',
    components: { Pagination }, //warehouseSelect orgSelect
    computed: {
        ...mapState({
            energy_type_unit: state => state.emsDict.energy_type_unit,
        })
    },
    data () {
        return {
            list: null,
            total: 0,
            theight: 0, //表格高度
            listQuery: {
                page: true,
                currentPage: 1,
                pageSize: 15,
                energyTypeCode: undefined,
                energyTypeName: undefined,
                remark: undefined
            },
            temp: {
                energyTypeCode: undefined,
                energyTypeName: undefined,
                energyTypeUnit: undefined,
                remark: undefined
            },
            dialogFormVisible: false,
            dialogStatus: '',
            tableKey: 0,
            rules: {
                energyTypeCode: [
                    {
                        required: true,
                        message: '能源分类编码不能为空',
                        trigger: 'blur'
                    },
                    { max: 50, message: '长度不能超过50', trigger: 'blur' }
                ],
                energyTypeName: [
                    {
                        required: true,
                        message: '能源分类名称不能为空',
                        trigger: 'blur'
                    },
                    { max: 50, message: '长度不能超过50', trigger: 'blur' }
                ],
                energyTypeUnit: [
                    {
                        required: true,
                        message: '能源分类名称不能为空',
                        trigger: 'blur'
                    },
                    { max: 50, message: '长度不能超过50', trigger: 'blur' }
                ],
                remark: [
                    { max: 50, message: '长度不能超过50', trigger: 'blur' }
                ]
            }
        }
    },
    mounted () {
        this.setTableHeight()
        this.$store.dispatch('emsDict/getDicData', ['energy_type_unit'])
        //表格高度自适应
        window.onresize = () => {
            this.setTableHeight()
        }
        this.getList()
    },
    destroyed () {
        this.setTableHeight()
        this.$store.dispatch('emsDict/getDicData', ['energy_type_unit'])
        this.getList()
    },
    methods: {
        getList () {
            api.queryEnergy(this.listQuery).then(res => {
                if (res.data) {
                    res.data.list.map((v, i) => {
                        v.serial = i + 1
                    })
                    this.list = res.data.list
                    this.total = res.data.pages.count
                }
            })
        },
        setTableHeight () {
            this.theight = this.$myFun.getSingleTbHeight()
        },
        handleQuery () {
            this.listQuery.currentPage = 1
            this.getList()
        },
        deleteQuery () {
            this.listQuery.currentPage = 1
            this.listQuery.energyTypeCode = undefined
            this.listQuery.energyTypeName = undefined
            this.getList()
        },
        handleCreate () {
            this.resetTemp()
            this.dialogStatus = 'create'
            this.dialogFormVisible = true
        },
        // 获取表格选中时的数据
        selectRow (val) {
            this.selectlistRow = val
        },
        //编辑弹窗
        handleUpdate () {
            if (this.selectlistRow && this.selectlistRow.length == 1) {
                this.temp = this.selectlistRow[0]
                api.getByCord(this.temp.energyTypeCode).then(res => {
                    this.temp = res.data
                    this.dialogStatus = 'update'
                    this.dialogFormVisible = true
                })
            } else {
                this.$message({
                    title: '警告',
                    message: '请选择一条信息',
                    type: 'warning'
                })
            }
        },
        update: throttle(
            function () {
                this.$refs['temp'].validate(valid => {
                    if (valid) {
                        api.updateEnergy(
                            this.temp.energyTypeCode,
                            this.temp
                        ).then(() => {
                            this.getList()
                            this.dialogFormVisible = false
                            this.$message({
                                title: '成功',
                                message: '更新成功',
                                type: 'success',
                                duration: 2000
                            })
                        })
                    }
                })
            },
            2000,
            {
                leading: true,
                trailing: false
            }
        ),
        create: throttle(
            function () {
                this.$refs.temp.validate(valid => {
                    if (valid) {
                        api.createEnergy(this.temp).then(() => {
                            this.handleChange()
                            this.getList()
                            this.dialogFormVisible = false
                            this.$notify({
                                title: '成功',
                                message: '新增成功',
                                type: 'success',
                                duration: 2000
                            })
                        })
                    } else {
                        return false
                    }
                })
            },
            2000,
            {
                leading: true,
                trailing: false
            }
        ),
        handleDelete () {
            if (this.selectlistRow && this.selectlistRow.length >= 1) {
                this.$confirm('此操作将删除所选中数据, 是否继续?', '提示', {
                    confirmButtonText: '确定',
                    cancelButtonText: '取消',
                    type: 'warning'
                })
                    .then(() => {
                        let ids = []
                        this.selectlistRow.forEach((val, index) => {
                            ids[index] = val.energyTypeCode
                        })
                        api.deleteEnergy(ids).then(() => {
                            this.getList(),
                                this.$message({
                                    title: '成功',
                                    message: '删除成功',
                                    type: 'success',
                                    duration: 2000
                                })
                        })
                    })
                    .catch(() => {
                        this.$message({
                            type: 'info',
                            message: '已取消删除'
                        })
                    })
            } else {
                this.$message({
                    title: '警告',
                    message: '请选择一条信息',
                    type: 'warning'
                })
            }
        },
        resetTemp () {
            this.temp = {
                energyTypeCode: undefined,
                energyTypeName: undefined,
                energyTypeUnit: undefined,
                remark: undefined
            }
        },
        handleClose () {
            this.resetTemp()
            this.$refs.temp.resetFields()
        },
        handleChange () {
            this.$emit('fresh', true)
        }
    }
}
