import * as api from "@/api/project/iot/IOT/project"
import employeesSelect from '@/components/Select/employeesSelect'
import orgSelect from '@/components/Select/orgSelect.vue'
import Slider from "@/components/Slider"
import throttle from 'lodash/throttle'
import { mapState } from 'vuex'
import { getProvince, getCity, getArea } from '@/utils/city'
export default {
    name: "projectMain",
    props: ['title'],
    components: { employeesSelect, orgSelect, Slider },
    computed: {
        ...mapState({
            iot_project_type: state => state.iotDict.iot_project_type
        })
    },
    watch: {
        'temp.province' (val) {
            this.cityList = getCity(val)
            if (this.cityList && this.cityList.length) {
                let arrIndex = this.cityList.find(i => {
                    return i.value == this.temp.city
                })
                if (!arrIndex) {
                    this.$set(this.temp, 'city', this.cityList[0].label)
                }
            } else {
                this.$set(this.temp, 'city', undefined)
            }
        },
        'temp.city' (val) {
            this.areaList = getArea(this.temp.province, val)
            if (this.areaList && this.areaList.length) {
                let arrIndex = this.areaList.find(i => {
                    return i.value == this.temp.county
                })
                if (!arrIndex) {
                    this.$set(this.temp, 'county', this.areaList[0].label)
                }
            } else {
                this.$set(this.temp, 'county', undefined)
            }
        }
    },
    data () {
        return {
            listLoading: false,
            theight: 0,//表格高度
            list: [],
            total: 0,
            listQuery: {
                page: true,
                currentPage: 1,
                pageSize: 4,
                projectName: undefined
            },
            dialogFormVisible: false,
            dialogFormType: undefined,
            dialogFormVisibleBatch: false, // 任务分配
            dialogFormVisibleEmployees: false, //人员列表弹出层
            dialogFormVisibleslider: false,//进度配置
            itemValue: undefined, //查询出的交付流程
            subPace: undefined,
            orderNumber: undefined,
            temp: {
                projectName: undefined,
                projectType: undefined,
                tenantOrgCode: undefined,
                tenantOrgName: undefined,
                projectManager: undefined,
                projectManagerName: undefined,
                province: undefined,
                city: undefined,
                county: undefined,
                remark: undefined,
            },
            batchTemp: {
                data: null
            },
            tempVal: undefined,
            rules: {
                projectName: [
                    {
                        required: true,
                        message: '项名称不能为空',
                        trigger: 'blur'
                    },
                    { max: 100, message: '长度不能超过100', trigger: 'blur' },
                    {
                        pattern: /^[\u4e00-\u9fa5]+$/,
                        message: '只允许中文输入',
                        trigger: 'blur'
                    }
                ],
                projectType: [
                    {
                        required: true,
                        message: '请选择项目类型',
                        trigger: 'change'
                    }
                ],
                tenantOrgCode: [{
                    required: true,
                    message: '请选择所属企业',
                    trigger: 'change'
                }],
                projectManagerName: [{
                    required: true,
                    message: '请选择项目经理',
                    trigger: 'change'
                }],
                province: [{
                    message: '请选择省份',
                    trigger: 'change'
                }],
                city: [{
                    message: '请选择城市',
                    trigger: 'change'
                }],
                county: [{
                    message: '请选择区',
                    trigger: 'change'
                }]
            },
            provinceList: getProvince(),
            cityList: null,
            areaList: null
        }
    },
    mounted () {
        this.setTableHeight()
        //表格高度自适应
        window.onresize = () => {
            this.setTableHeight()
        }
        this.getList()
    },
    methods: {
        setPage (code, page) {
            let path = ''
            let name = ''
            let params = {
                code,
                page
            }
            if (page == 'EM') {
                path = `/project/IOT/IOT/IOTEM`
                name = `IOTEM`
            } else if (page == 'GA') {
                path = `/project/IOT/IOT/IOTGR`
                name = `IOTGR`
            } else if (page == 'RW') {
                path = `/project/IOT/IOT/IOTRW`
                name = `IOTRW`
            } else {
                return this.$message({
                    title: this.$t('message.warning'),
                    message: "该功能暂未开发！",
                    type: "warning"
                })
            }
            this.$router.push({ path, params, name })
            // this.$emit('setCode', code)
            // this.$emit('setPage', page)
        },
        handleSizeChange (val) {
            this.listQuery.pageSize = val
            this.getList()
        },
        handleCurrentChange (val) {
            this.listQuery.currentPage = val
            this.getList()
        },
        //表格高度计算
        setTableHeight () {
            this.theight = this.$myFun.getSingleTbHeight()
        },
        //获取表格数据
        getList () {
            this.listLoading = true
            api.queryProjects(this.listQuery).then(response => {
                this.list = response.data.list
                this.total = response.data.pages.count
                setTimeout(() => {
                    this.listLoading = false
                }, 1.5 * 100)
            })
        },
        //表格查询
        handleQuery () {
            this.listQuery.currentPage = 1
            this.getList()
        },
        //新增按钮单击事件方法
        handleCreate () {
            this.reTemp()
            this.dialogFormType = 'create'
            this.dialogFormVisible = true
        },
        create: throttle(
            function () {
                this.$refs.tempRef.validate(valid => {
                    if (valid) {
                        api.addProject(this.temp).then(() => {
                            this.getList()
                            this.dialogFormVisible = false
                            this.reTemp()
                            this.$notify({
                                title: '成功',
                                message: '新增成功',
                                type: 'success',
                                duration: 2000
                            })
                        })
                    } else {
                        return false
                    }
                })
            },
            2000,
            {
                leading: true,
                trailing: false
            }
        ),
        handleUpdate (code) {
            api.queryProject(code).then(res => {
                this.temp = res.data
                this.dialogFormType = 'update'
                this.dialogFormVisible = true
            })
        },
        update: throttle(
            function () {
                this.$refs.tempRef.validate(valid => {
                    if (valid) {
                        api.updateProject(this.temp.projectCode, this.temp).then(() => {
                            this.getList()
                            this.dialogFormVisible = false
                            this.reTemp()
                            this.$notify({
                                title: '成功',
                                message: '修改成功',
                                type: 'success',
                                duration: 2000
                            })
                        })
                    } else {
                        return false
                    }
                })
            },
            2000,
            {
                leading: true,
                trailing: false
            }
        ),
        //删除按钮单击事件方法
        handleDelete (code) {
            this.$confirm('此操作将删除所选中数据, 是否继续?', '提示', {
                confirmButtonText: '确定',
                cancelButtonText: '取消',
                type: 'warning'
            })
                .then(() => {
                    api.deleteProject(code).then(() => {
                        this.getList()
                        this.$message({
                            title: '成功',
                            message: '删除成功',
                            type: 'success',
                            duration: 2000
                        })
                    }).catch(() => {
                        this.getList()
                    })
                })
                .catch(() => {
                    this.$message({
                        type: 'info',
                        message: '已取消删除'
                    })
                })
        },
        // 任务分配
        handleDistribution (code) {
            api.queryProject(code).then(res => {
                this.temp = res.data
                this.batchTemp.data = res.data.projectDeliveryDTOS
                this.dialogFormVisibleBatch = true
            })
        },
        distribution: throttle(
            function () {
                api.queryBatch(this.temp.projectCode, this.batchTemp.data).then(() => {
                    this.getList()
                    this.dialogFormVisibleBatch = false
                    this.$notify({
                        title: '成功',
                        message: '修改成功',
                        type: 'success',
                        duration: 2000
                    })
                })
            },
            2000,
            {
                leading: true,
                trailing: false
            }
        ),
        // 获取负责人
        getBatchEmployee (orderNumber) {
            this.dialogFormVisibleEmployees = true
            this.orderNumber = orderNumber
        },
        //获取人员
        getEmployee (val) {
            this.tempVal = val
        },
        handleEmployee () {
            if (this.dialogFormVisibleBatch) {
                this.batchTemp.data.forEach(item => {
                    if (item.orderNumber == this.orderNumber) {
                        item.projectManager = this.tempVal.employeeCode
                        item.projectManagerName = this.tempVal.employeeName
                    }
                })
            } else {
                this.$set(this.temp, 'projectManager', this.tempVal.employeeCode)
                this.$set(this.temp, 'projectManagerName', this.tempVal.employeeName)
            }
            this.orderNumber == undefined
            this.dialogFormVisibleEmployees = false
        },
        // 进度配置弹窗
        show: throttle(
            function (projectCode, orderNumber) {
                api.queryOrderNumber(projectCode, orderNumber).then(res => {
                    this.itemValue = res.data
                    this.subPace = Number(JSON.parse(JSON.stringify(res.data.pace)))
                    this.dialogFormVisibleslider = true
                })
            },
            2000,
            {
                leading: true,
                trailing: false
            }
        ),
        getPace (val) {
            this.itemValue.pace = val
            this.subPace = val
        },
        handleUpdatePace: throttle(
            function () {
                api.updateOrderNumber(this.itemValue.projectCode, this.itemValue.orderNumber, this.itemValue).then(
                    () => {
                        this.getList()
                        this.dialogFormVisibleslider = false
                        return this.$notify({
                            title: '成功',
                            message: '修改成功',
                            type: 'success',
                            duration: 2000
                        })
                    }
                )
            },
            2000,
            {
                leading: true,
                trailing: false
            }
        ),
        reTemp () {
            this.temp = {}
        }
    }
};
