import * as api from "@/api/project/srm/priceManage/purchasePriceInquiry"
import Pagination from "@/components/Pagination"
import { mapState } from "vuex"
export default {
    components: { Pagination },
    props: ['orgCode', 'supplierCode', 'validTimeStart', 'validTimeEnd'],
    data () {
        return {
            dialogVisible: false,
            tableKey: 0,
            list: [],
            total: 0,
            listQuery: {
                orgCode: undefined,
                supplierCode: undefined,
                sectionTimeStart: undefined,
                sectionTimeEnd: undefined,
                page: true,
                currentPage: 1,
                pageSize: 10,
            },

        }
    },

    computed: {
        ...mapState({
            dt_srm_p0: state => state.dict.dt_srm_p0,
            dt_srm_pdv: state => state.dict.dt_srm_pdv,
        })
    },
    mounted () {
        this.$store.dispatch("dict/getDicData", ["dt_srm_p0", 'dt_srm_pdv'])
        this.getList()
    },
    watch: {
        orgCode: {
            handler (newValue) {
                this.listQuery.orgCode = newValue
                this.getList()
            },
            deep: true
        },
        supplierCode: {
            handler (newValue) {
                this.listQuery.supplierCode = newValue
                this.getList()
            },
            deep: true
        },
        validTimeStart: {
            handler (newValue) {
                this.listQuery.sectionTimeStart = newValue
                this.getList()
            },
            deep: true
        },
        validTimeEnd: {
            handler (newValue) {
                this.listQuery.sectionTimeEnd = newValue
                this.getList()
            },
            deep: true
        },
    },
    methods: {
        getList () {
            api.queryRecords(this.listQuery).then(res => {
                console.log(res, 'kkkk')
                let options = [this.dt_srm_p0]
                res.data.list = this.$myFun.codeToName(res.data.list, options, ['status'])
                this.list = res.data.list
                this.total = res.data.pages.count
            })
        },
        handleQuery () {
            this.getList()
        },
        handleDelete () {

        },
        confirm () {
            if (this.selectlistRow) {
                console.log(this.selectlistRow, 'kkk')
                this.$emit('handeTemplate', this.selectlistRow)
                this.dialogVisible = false
            } else {
                this.$message({
                    title: '警告',
                    message: '请选择一条信息',
                    type: 'warning',
                    duration: 2000
                })
            }
        },
        // 表格选择框选中 注:参数为选中的所有行的数组
        selectChange (val) {
            this.selectlistRow = val[val.length - 1]
            if (val.length > 1) {
                this.$refs.tb.clearSelection()
                this.$refs.tb.toggleRowSelection(val.pop())
            }
            this.$refs.tb.setCurrentRow(this.selectlistRow)
        },
        //点击表格某一行
        rowClick (val) {
            this.$refs.tb.clearSelection()
            this.$refs.tb.toggleRowSelection(val)
        }
    }
}
