import capsuleBar from './capsuleBar'
import lineBox from './line'
export default {
    name: "EASEm",
    data () {
        return {
            theight: 0,
            inputValue: undefined,
            barData: undefined,
            lineData: undefined,
            list: [
                { code: 'WL0001', name: '备件1', time1: '20天', date: '2020-12-08', time2: '12个月', number: '8' },
                { code: 'WL0002', name: '备件2', time1: '30天', date: '2020-10-08', time2: '7个月', number: '13' },
            ],
            data: [{
                label: 'xx项目',
                children: [
                    {
                        label: '产品1',
                        children: [
                            { label: '设备1' },
                            { label: '设备2' },
                            { label: '设备3' }
                        ]
                    },
                    {
                        label: '产品2',
                        children: [
                            { label: '设备1' },
                            { label: '设备2' },
                            { label: '设备3' },
                            { label: '设备4' },
                            { label: '设备5' },
                        ]
                    }
                ]
            }]
            ,
            defaultProps: {
                children: 'children',
                label: 'label'
            }
        }
    },
    components: { capsuleBar, lineBox },
    mounted () {
        this.setTableHeight()
        //表格高度自适应
        window.onresize = () => {
            this.setTableHeight()
        }
        setTimeout(() => {
            this.setData()
        }, 1000)
    },
    methods: {
        setData () {
            this.barData = {
                yAxisData: ['设备生命周期'],
                data: [63]
            }

            let data1 = []
            let data2 = []
            for (let i = 0; i < 100; i++) {
                data1.push(getRend(2000, 1500))
                data2.push(getRend(2500, 2300))
            }
            this.lineData = {
                data1: data1,
                data2: data2,
            }
            function getRend (m, n) {
                return Math.floor(Math.random() * (m - n + 1) + n)
            }
        },
        setTableHeight () {
            let wh = document.body.offsetHeight
            let fch = 0
            if (document.getElementsByClassName('fixed-header')[0]) {
                fch = document.getElementsByClassName('fixed-header')[0]
                    .offsetHeight + 2
            }
            this.theight = wh - fch
        },
    },
}
