import Pagination from "@/components/Pagination"
import { mapState } from "vuex"
import dialogGysxz from '@/components/Dialog/dialogsGysxz'//供应商选择
import addMain from './dialog/addMain'
import { DICT_CODE } from '@/utils/constant'
import caiGou from '@/components/Select/xtpt/caigou'//下拉组件
import dialogWlxzbycglx from '@/components/Dialog/dialogWlxzbycglx.vue'//物料选择
export default {
    name: "quotaApplication",
    components: { Pagination, dialogGysxz, addMain, caiGou, dialogWlxzbycglx },
    data () {
        return {
            DICT_CODE: DICT_CODE,
            theight: 0,
            isSingle: true,//表格是否单选 点击各按钮根据流程逻辑控制单多选
            currentSelectedRow: null,//当前选中的行
            selectedRows: null,//多选时选中的所有行
            list: null,
            listSub: null,
            total: 0,
            selectedrow: null,//主表选中行
            listQuery: {
                page: true,
                currentPage: 1,
                pageSize: 10,
                supplierName: undefined
            },
            sublistQuery: {
                page: true,
                currentPage: 1,
                pageSize: 10
            },
            tableKey: [0, 1]//表格索引，虚拟dom渲染表格时使用

        }
    },
    computed: {
        ...mapState({
            dt_srm_p0: state => state.dict.dt_srm_p0,

        })
    },
    mounted () {
        this.$store.dispatch("dict/getDicData", ["dt_srm_pdv", 'dt_srm_pdw', 'dt_srm_pdx', 'dt_srm_p0'])
        this.setTableHeight()
        //表格高度自适应
        window.onresize = () => {
            this.setTableHeight()
        }

        this.getList()
    },
    methods: {
        //表格高度计算
        setTableHeight () {
            this.theight = this.$myFun.getSingleTbHeight() - 185
        },
        showGys () {
            this.$refs.refGys.dialogVisible = true
            this.$refs.refGys.getList()
        },
        //供应商选择确定
        handleGysClick (val) {
            console.log(val)
            this.$set(this.listQuery, 'supplierName', val[0].supplierName)
            this.$set(this.listQuery, 'supplierCode', val[0].supplierCode)
        },
        showWl () {
            this.$refs.wlxz.dialogVisible = true
            this.$refs.wlxz.getList()
        },
        confirmWlxz (data) {
            console.log(data)
            if (data) {
                this.$set(this.listQuery, 'materialCode', data[0].materialCode)
                this.$set(this.listQuery, 'materialName', data[0].materialName)
            }
        },
        checked () {
            this.$refs.tb.toggleRowSelection(this.list[0], true)//官网方法，重点
        },
        //显示主表
        getList () {
            this.list = [
                { number1: 'PV采购组织', number2: 'X6', number3: 'X6', number4: '有效', number5: '黄渤', number6: '2019-10-23 10:18:58' }
            ]
            this.$nextTick(function () {
                this.checked()
            })
        },
        //显示从表
        getSubList () {
            this.listSub = [
                { params0: '1', params1: 'X6_QC2018051601', params2: '01', params3: 'X6_QC2018051601', params4: '10', params5: '黄渤', params6: '2019-10-22 11:28:36', params7: '黄渤', params8: '2019-10-22 11:28:36' }
            ]
        },
        //主表删除按钮单击事件
        handleDelete () {
            if (this.selectlistRow) {
                this.$confirm("是否删除该信息？", "提示", {
                    confirmButtonText: "确定",
                    cancelButtonText: "取消",
                    type: "warning"
                }).then(() => {
                    // api.deleteRecord(val.rowId).then(() => {
                    //     this.$message({
                    //         title: '成功',
                    //         message: '生效成功',
                    //         type: 'success',
                    //         duration: 2000
                    //     })
                    //     this.getList()
                    // })
                }).catch(() => {
                    this.$message({
                        type: "info",
                        message: "已取消"
                    })
                })
            } else {
                this.$message({
                    title: "警告",
                    message: "请选择一条信息",
                    type: "warning"
                })
            }
        },
        //生效按钮单击事件
        handleEffect () {
            if (this.selectlistRow) {
                this.$confirm("是否确认生效产品基准价格清单？", "提示", {
                    confirmButtonText: "确定",
                    cancelButtonText: "取消",
                    type: "warning"
                }).then(() => {
                    // api.deleteRecord(val.rowId).then(() => {
                    //     this.$message({
                    //         title: '成功',
                    //         message: '生效成功',
                    //         type: 'success',
                    //         duration: 2000
                    //     })
                    //     this.getList()
                    // })
                }).catch(() => {
                    this.$message({
                        type: "info",
                        message: "已取消"
                    })
                })
            } else {
                this.$message({
                    title: "警告",
                    message: "请选择一条信息",
                    type: "warning"
                })
            }
        },
        //新增按钮单击事件
        handleCreate () {
            this.$refs.addMain.dialogStatus = 'create'
            this.$refs.addMain.dialogVisible = true
        },
        //编辑按钮单击事件
        handleUpdate (index, val) {
            this.$refs.addMain.dialogStatus = 'update'
            this.$refs.addMain.dialogVisible = true
            this.$refs.addMain.temp = { ...val }
        },
        //删除
        handleDeleteItems (index, val) {
            console.log(index, val)
            this.$confirm("是否删除该信息", "提示", {
                confirmButtonText: "确定",
                cancelButtonText: "取消",
                type: "warning"
            }).then(() => {
                // api.deleteRecord(val.rowId).then(() => {
                //     this.$message({
                //         title: '成功',
                //         message: '删除成功',
                //         type: 'success',
                //         duration: 2000
                //     })
                //     this.getList()
                // })
            }).catch(() => {
                this.$message({
                    type: "info",
                    message: "已取消"
                })
            })

        },
        //提交按钮单击事件
        handleSubmit () {

        },
        //查询
        handleQuery () {
            this.listSub = []
            this.listQuery.currentPage = 1
            this.getList()
        },
        // 表格选择框选中 注:参数为选中的所有行的数组
        selectChange (val) {
            this.selectlistRow = val[val.length - 1]
            if (val.length > 1) {
                this.$refs.tb.clearSelection()
                this.$refs.tb.toggleRowSelection(val.pop())
            }
            if (this.selectlistRow) {
                this.getSubList()
            }
            this.$refs.tb.setCurrentRow(this.selectlistRow)
        },
        //点击表格某一行
        rowClick (val) {
            this.$refs.tb.clearSelection()
            this.$refs.tb.toggleRowSelection(val)
        },
    }
};
