import { mapState } from 'vuex';
import { DT_ORG_TYPE } from '@/utils/constant'
import warehouseSelect from '@/components/Select/warehouse-select.vue'//下拉组件
import caiGou from '@/components/Select/caigou.vue'//下拉组件

export default {
    name: 'addMain',
    components: { warehouseSelect, caiGou },
    props: ['closeData'],
    data () {
        return {
            DT_ORG_TYPE: DT_ORG_TYPE,
            dialogCloseVisible: false,
            rules: [],
            selectRows: []
        }
    },
    created () {

    },
    computed: {
        ...mapState({
            dt_pullmaterial_type: state => state.dict.dt_pullmaterial_type,
        })
    },
    mounted () {
        this.$store.dispatch('dict/getDicData', ['dt_pullmaterial_type']);
    },
    methods: {

        //添加项
        addSub () {
            this.$emit('showWlxz');
        },
        //关闭
        handleClose () {
            if (this.selectRows) {
                if (this.selectRows.pullMaterialState) {
                    this.$confirm(this.$t('message.isCancellation'), this.$t('message.prompt'), {
                        confirmButtonText: this.$t('message.confirm'),
                        cancelButtonText: this.$t('message.cancel'),
                        type: 'warning'
                    }).then(() => {

                        this.$message({
                            type: 'success',
                            message: this.$t('message.successed')
                        });
                    }).catch(() => {
                        this.$message({
                            type: 'info',
                            message: this.$t('message.canceled')
                        });
                    });
                } else {
                    this.$message.warning(this.$t('message.checkedplease'));
                    return false;
                }
            } else {
                this.$message.warning(this.$t('message.checkedplease'));
                return false;
            }
        },
        closeSelectChange (val) {
            this.selectRows = val[val.length - 1];
            if (val.length > 1) {
                this.$refs.closetb.clearSelection()
                this.$refs.closetb.toggleRowSelection(val[val.length - 1], 'selected')
            }

            if (val.length !== 0) {
                this.getSubList(val[val.length - 1]);
            }
        }
    }
}
