import * as api from '@/api/project/mes/dataBase/stationEquipment/stationEquipmentSelect'
import Pagination from '@/components/Pagination'
import { mapState } from 'vuex'

export default {
    name: 'stationEquipmentSelect',
    components: { Pagination },
    props: ['orgCode', 'unMutiple', 'stockType', 'warehouseCode'],
    data() {
        return {
            list: null,
            total: 0,
            supplierData: null,
            listQuery: {
                currentPage: 1,
                pageSize: 10
            },
            temp: {},
            activeName2: 'first',
            listSelected: [],
            tableKey: 0,
            allowSelect: false
        }
    },
    watch: {
        // 监听时间区间数组 写入到对应字段
        listSelected(value) {
            this.$parent.$parent.$parent.list_b = value
            console.log(this.$parent.$parent.$parent.list_b)
        }
    },
    computed: {
        ...mapState({
            dt_stock_type: state => state.dict.dt_stock_type,
            dt_stock_status: state => state.dict.dt_stock_status,
            dt_materiel_type: state => state.dict.dt_materiel_type
        })
    },
    mounted() {
        this.$store.dispatch('dict/getDicData', [
            'dt_stock_type',
            'dt_stock_status',
            'dt_materiel_type',
            'dt_stock_type'
        ])
        this.getEquipmentData()
        this.suppierRecords()
    },
    methods: {
        suppierRecords() {
            api.querySuppierRecords().then(res => {
                this.supplierData = res.data.list
            })
        },
        checked(i) {
            this.$refs.tb_bx.toggleRowSelection(this.list[i], true)
        },
        handleQuery() {
            this.listQuery.currentPage = 1
            this.getEquipmentData()
            this.listSelected = []
        },
        // 父级工位表格展示已选，modal已选勾选
        getListSelected() {
            this.listSelected = this.$parent.$parent.$parent.list_b
        },
        selectMain(val) {
            // 有选中检查是否已选
            for (let i = 0; i < this.listSelected.length; i++) {
                if (this.listSelected[i].equipmentCode == val.equipmentCode) {
                    // 在已选中去除该条
                    this.popSelected(val)
                    return false
                }
            }
            // 类型检查
            if (Array.isArray(val)) {
                return false
            }
            // 加入已选并勾选框
            this.listSelected.push(val)
            this.$refs.tb_bx.toggleRowSelection(val, true)
        },
        // 去除已选
        popSelected(val) {
            this.listSelected.forEach((item, index) => {
                if (item.equipmentCode == val.equipmentCode) {
                    this.listSelected.splice(index, 1)
                    this.$refs.tb_bx.toggleRowSelection(val, false)
                }
            })
        },
        selectAll(val) {
            if (val.length == 0) {
                this.listSelected = []
                this.$refs.tb_bx.clearSelection()
                return false
            }
            val.forEach((items, indexv) => {
                let isSelected = false
                this.listSelected.forEach(item => {
                    if (item.equipmentCode === items.equipmentCode) {
                        isSelected = true
                    }
                })
                if (!isSelected) {
                    this.listSelected.push(val[indexv])
                }
            })
        },
        // 在备选中把已选打钩
        getEquipmentData() {
            api.getEquipmentAll(this.listQuery).then(res => {
                this.list = res.data.list
                this.total = res.data.pages.count
                // 已选中的勾选状态
                this.listSelected.forEach(item => {
                    for (let i = 0; i < this.list.length; i++) {
                        // console.log(
                        //     item.equipmentCode,
                        //     this.list[i].equipmentCode
                        // )
                        if (item.equipmentCode == this.list[i].equipmentCode) {
                            this.$nextTick(() => {
                                this.$refs.tb_bx.toggleRowSelection(
                                    this.list[i],
                                    true
                                )
                            })
                            break
                        }
                    }
                })
            })
        }
    }
}
