import * as api from "@/api/project/srm/contractManage/paymentMethod"
import { DICT_CODE } from '@/utils/constant'
import caiGou from '@/components/Select/xtpt/caigou'//下拉组件
import { mapState } from "vuex"
import throttle from 'lodash/throttle'

export default {
    components: { caiGou },
    data () {
        return {
            Edit: false,//控制编辑弹框可编辑项
            DICT_CODE: DICT_CODE,
            dialogStatus: '',
            dialogVisible: false,
            temp: {
                rowId: undefined
            },
            rules: {
                purchaseOrg: [{ required: true, message: '采购组织不能为空', trigger: 'blur' }],
                year: [{ required: true, message: '年度不能为空', trigger: 'blur' }],
                paymentType: [{ required: true, message: '付款方式不能为空', trigger: 'blur' }],
                settlementType: [{ required: true, message: '结算方式不能为空', trigger: 'blur' }],
                paymentPeriod: [{ required: true, message: '付款周期不能为空', trigger: 'blur' }],
            }
        }
    },
    computed: {
        ...mapState({
            dt_srm_pdr: state => state.dict.dt_srm_pdr,
            dt_srm_pdt: state => state.dict.dt_srm_pdt,
            dt_srm_pdu: state => state.dict.dt_srm_pdu,
        })
    },
    mounted () {
        this.$store.dispatch("dict/getDicData", ["dt_srm_pdr", 'dt_srm_pdt', 'dt_srm_pdu'])
    },
    methods: {
        //获取采购组织名称
        selectName (val) {
            this.temp.purchaseOrgName = val
        },
        //新增弹框确定事件
        createData: throttle(function () {
            this.$refs.dataForm.validate(valid => {
                if (valid) {
                    api.addRecord(this.temp).then(() => {
                        this.$message({
                            title: '成功',
                            message: '新增成功',
                            type: 'success',
                            duration: 2000
                        })
                        this.dialogVisible = false
                        this.$emit('refresh')
                    })
                } else {
                    return false
                }
            })
        }, 2000,
            {
                leading: true,
                trailing: false
            }
        ),
        //编辑弹框确定事件
        updateData: throttle(function () {
            api.updateRecord(this.temp.rowId, this.temp).then(() => {
                this.$message({
                    title: '成功',
                    message: '编辑成功',
                    type: 'success',
                    duration: 2000
                })
                this.dialogVisible = false
                this.$emit('refresh')
            })
        }, 2000,
            {
                leading: true,
                trailing: false
            }
        ),
        resetTemp () {
            this.temp = {
                purchaseOrg: undefined,
                year: undefined,
                paymentType: undefined,
                settlementType: undefined,
                paymentPeriod: undefined,
                illustrate: undefined,
                remark: undefined
            }
        }
    }
}
