import * as api from "@/api/project/srm/dataBase/specialDistinction"
import { DICT_CODE } from '@/utils/constant'
import caiGou from '@/components/Select/xtpt/caigou'//下拉组件
import Pagination from '@/components/Pagination'
import addMain from './dialog/addMain'
import { mapState } from "vuex"

export default {
    name: 'specialDistinction',
    components: { Pagination, caiGou, addMain },
    data () {
        return {
            DICT_CODE: DICT_CODE,
            list: null,
            total: 0,
            theight: 0, //表格高度
            listQuery: {
                page: true,
                currentPage: 1,
                pageSize: 15,
                colorCode: undefined,
                colorName: undefined
            },
            tableKey: 0,
        }
    },
    computed: {
        ...mapState({
            dt_srm_pds: state => state.dict.dt_srm_pds,
        })
    },
    mounted () {
        this.$store.dispatch("dict/getDicData", ["dt_srm_pds"])
        this.setTableHeight()
        //表格高度自适应
        window.onresize = () => {
            this.setTableHeight()
        }
        this.getList()
    },
    watch: {
        'dt_srm_pds': function () {
            this.getList()
        }
    },
    methods: {
        refresh () {
            this.getList()
        },
        getList () {
            api.queryRecords(this.listQuery).then(res => {
                let options = [this.dt_srm_pds]
                res.data.list = this.$myFun.srmCodeToName(res.data.list, options, ['usageState'])
                this.list = res.data.list
                this.total = res.data.pages.count
            })
        },
        //表格高度计算
        setTableHeight () {
            this.theight = this.$myFun.getSingleTbHeight()
        },
        handleQuery () {
            //查询
            this.listQuery.currentPage = 1
            this.getList()
        },
        handleCreate () {
            //新增
            this.$refs.addMain.resetTemp()
            this.$refs.addMain.dialogStatus = 'create'
            this.$refs.addMain.dialogVisible = true
        },
        //编辑按钮单击事件
        handleUpdate (index, val) {
            this.$refs.addMain.dialogStatus = 'update'
            this.$refs.addMain.temp = { ...val }
            this.$refs.addMain.dialogVisible = true
        },

        handleDelete (index, val) {
            //删除列表
            this.$confirm("是否删除该信息", "提示", {
                confirmButtonText: "确定",
                cancelButtonText: "取消",
                type: "warning"
            }).then(() => {
                api.deleteRecord(val.rowId).then(() => {
                    this.$message({
                        title: '成功',
                        message: '删除成功',
                        type: 'success',
                        duration: 2000
                    })
                    this.getList()
                })
            }).catch(() => {
                this.$message({
                    type: "info",
                    message: "已取消"
                })
            })

        },
    }
}
