import { queryRecords as getWorkCenter } from '@/api/project/eas/database/work-center'
export default {
    name: 'useDeptSelect',
    props: {
        workCenterClass: {
            //工位类别：从组织结构中查询组织类型为“生产线”的组织信息
            type: String,
            default: ''
        },
        workCenterCode: {
            type: String,
            default: ''
        },
        disabled: {
            type: Boolean,
            default: false
        },
        placeholder: {
            type: String,
            default: '请选择'
        }
    },
    model: {
        prop: 'workCenterCode',
        event: 'change'
    },
    data() {
        return {
            options: [],
            itemKey: this.workCenterCode
        }
    },
    watch: {
        workCenterCode() {
            this.itemKey = this.workCenterCode
        }
    },
    created() {
        this.getOptions()
    },
    methods: {
        getOptions() {
            getWorkCenter({
                workCenterClass: this.workCenterClass
            }).then(res => {
                this.options = res.data
            })
        },
        changeSelect(val) {
            this.$emit('change', val)
            let name = ''
            let org = this.options
                .filter(v => v.workCenterCode == val)
                .find(value => value)
            if (org) {
                name = org.workCenterName
            }

            this.$emit('selectName', name)
        }
    }
}
