import Pagination from "@/components/Pagination"
import { mapGetters } from 'vuex'
import addMain from "./dialog/addMain"
import appendix from "./dialog/appendix"
import examine from "./dialog/examine"



export default {
    name: "supplierfourMChange",
    components: { Pagination, addMain, appendix, examine },
    data () {
        return {
            modalnum: null,//模板编号
            theight: 0,
            isSingle: true,//表格是否单选 点击各按钮根据流程逻辑控制单多选
            currentSelectedRow: null,//当前选中的行
            selectedRows: null,//多选时选中的所有行
            list: null,
            listSub: null,
            total: 0,
            selectedrow: null,//主表选中行
            dialogVisible: false,
            dialogVisiblefj: false,
            dialogVisiblesh: false,
            dialogStatus: "",
            listQuery: {
                page: true,
                currentPage: 1,
                pageSize: 10
            },
            sublistQuery: {
                page: true,
                currentPage: 1,
                pageSize: 10
            },
            tableKey: [0, 1]//表格索引，虚拟dom渲染表格时使用

        }
    },
    computed: {
        ...mapGetters([
            'domainName',
            'domainId',
        ])
    },
    mounted () {
        this.setTableHeight()
        //表格高度自适应
        window.onresize = () => {
            this.setTableHeight()
        }
        this.listQuery.state = 'CREATE'
        this.getDelivery()
    },
    methods: {
        changeMoudle (val) {
            this.$emit('changeSelect', val)
        },
        //表格高度计算
        setTableHeight () {
            this.theight = this.$myFun.getSingleTbHeight() - 185
        },
        //主表数据获取
        getDelivery () {
            if (this.listQuery.supplierCode) {
                // api.getDelivery(this.listQuery).then(res => {
                //     this.list = res.list;
                //     this.total = res.pages.count;
                //     this.setFirstLine();
                //     if (this.list[0]) {
                //         this.getSubList(this.list[0])
                //     }
                // });
            }
        },
        //显示主表
        getList () {
            this.getDelivery()
        },
        //显示从表
        getSubList (row) {
            this.sublistQuery.supplierCode = row.supplierCode
            this.sublistQuery.orderNumber = row.orderNumber
            // api.getDeliveryDetail(this.sublistQuery).then(res => {
            //     this.listSub = res.deliveryOrderItemDTOList;
            // });
        },
        handleCreate () {
            this.dialogStatus = "create"
            this.dialogVisible = true
        },
        handleAppendix () {
            this.dialogVisiblefj = true
        },
        handleExamine () {
            this.dialogVisiblesh = true
        },
        //发布
        handlePublish () {

        },
        //删除
        handleDelete () {

        },
        //查询
        handleQuery () {
            this.listSub = []
            this.listQuery.currentPage = 1
            this.getList()
        },
        //每页大小变化
        handleSizeChange (val) {
            this.listQuery.pageSize = val
            this.getList()
        },
        //当前页变化
        handleCurrentChange (val) {
            this.listQuery.currentPage = val
            this.getList()
        },
        // 表格选择框选中 注:参数为选中的所有行的数组
        selectChange (val) {
            if (this.isSingle) {
                if (val.length > 1) {
                    this.$refs.tb.clearSelection() //清除其他行的选中
                    this.$refs.tb.toggleRowSelection(val[val.length - 1], "selected") //单击行绑定点击事件
                } else if (val.length === 1) {
                    this.selectedRows = val
                    this.currentSelectedRow = val[val.length - 1]
                }
            } else {
                this.selectedRows = val
                this.currentSelectedRow = val[val.length - 1]
            }
        },
        //点击表格某一行
        rowClick (val) {
            if (this.isSingle) {
                this.$refs.tb.clearSelection() //清除其他行的选中
            }
            this.$refs.tb.toggleRowSelection(val, "selected") //单击行绑定点击事件
            this.getSubList(val)
        },
        //加载时默认选中第一行
        setFirstLine () {
            this.$nextTick(() => {
                this.$refs.tbmain.setCurrentRow(this.list[0])
            })
        }
    }
};
