import * as api from "@/api/project/srm/contractManage/problemManagement"
import throttle from 'lodash/throttle'
import { mapState } from "vuex"
export default {
    components: {},
    data () {
        return {
            dialogVisible: false,
            temp: {
                status: 'PCL0',
                auditStateCode: undefined,
                auditExplain: undefined,
                rowId: undefined,
            },
            rules: {
                auditStateCode: [{ required: true, message: '审核状态不能为空', trigger: 'blur' }],
                auditExplain: [{ message: '审核说明不能为空', trigger: 'blur' }],
            }
        }
    },
    computed: {
        ...mapState({
            dt_srm_pcl: state => state.dict.dt_srm_pcl,
        })
    },
    mounted () {
        this.$store.dispatch("dict/getDicData", ['dt_srm_pcl'])
    },
    methods: {
        handleClose () {
            this.$refs.dataForm.resetFields()
        },
        getStatus (val) {
            this.status = val
        },
        //新增确定按钮单击事件
        createData: throttle(function () {
            this.$refs.dataForm.validate(valid => {
                if (valid) {
                    api.examineRecord(this.temp).then(() => {
                        this.$message({
                            title: '成功',
                            message: '审核成功',
                            type: 'success',
                            duration: 2000
                        })
                        this.dialogVisible = false
                        this.$emit('refresh')
                    })
                } else {
                    return false
                }
            })
        }, 2000,
            {
                leading: true,
                trailing: false
            }),
