import * as api from '@/api/project/mes/dataBase/stationProduct/stationProductSelect2'
import Pagination from '@/components/Pagination'
import { mapState } from 'vuex'

export default {
    name: 'stationProductSelect',
    components: { Pagination },
    data() {
        return {
            list: null,
            total: 0,
            supplierData: null,
            listQuery: {
                currentPage: 1,
                pageSize: 10
            },
            activeName2: 'first',
            listSelected: [],
            tableKey: 0
        }
    },
    filters: {
        isEnableFilter: function(e) {
            if (e === 'step_1') {
                return '是'
            } else {
                return '否'
            }
        }
    },
    watch: {
        listSelected(value) {
            this.$parent.$parent.$parent.list_c = value
        }
    },
    computed: {
        ...mapState({
            dt_inspection_type: state => state.mesDict.dt_inspection_type,
            dt_inspection_method: state => state.mesDict.dt_inspection_method
        })
    },
    mounted() {
        this.$store.dispatch('mesDict/getDicData', [
            'dt_inspection_type',
            'dt_inspection_method'
        ])
    },
    methods: {
        getListSelected() {
            this.listSelected = this.$parent.$parent.$parent.list_c
        },
        checked(i) {
            this.$refs.tb_bx.toggleRowSelection(this.list[i], true)
        },
        handleQuery() {
            this.listQuery.currentPage = 1
            this.getEquipmentData()
        },
        //检查是否取消勾选
        checkUnsel(val, row) {
            let isuncheck = true
            val.forEach(item => {
                if (item.inspectionCode === row.inspectionCode) {
                    isuncheck = false
                }
            })
            return isuncheck
        },
        selectMain(val, row) {
            let isuncheck = this.checkUnsel(val, row)
            if (!isuncheck) {
                if (this.listSelected) {
                    val.forEach((items, indexv) => {
                        let isSelected = false
                        this.listSelected.forEach(item => {
                            if (item.inspectionCode === items.inspectionCode) {
                                isSelected = true
                            }
                        })
                        if (!isSelected) {
                            this.listSelected.push(val[indexv])
                        }
                    })
                } else {
                    this.listSelected = val
                }
            } else {
                this.listSelected = this.listSelected.filter(item => {
                    return item.inspectionCode !== row.inspectionCode
                })
            }
        },
        selectAll(val) {
            if (val.length == 0) {
                this.list.forEach(item => {
                    this.listSelected.forEach((item2, index) => {
                        if (item.inspectionCode == item2.inspectionCode) {
                            this.listSelected.splice(index, 1)
                        }
                    })
                })
                return false
            }
            val.forEach((items, indexv) => {
                let isSelected = false
                this.listSelected.forEach(item => {
                    if (item.inspectionCode === items.inspectionCode) {
                        isSelected = true
                    }
                })
                if (!isSelected) {
                    this.listSelected.push(val[indexv])
                }
            })
        },
        // 去除已选
        getEquipmentData() {
            api.queryRecords(this.listQuery).then(res => {
                res = res.data
                this.list = this.$myFun.codeToName(
                    res,
                    [this.dt_inspection_method, this.dt_inspection_type],
                    ['inspectionMode', 'inspectionType']
                ).list
                this.total = res.pages.count
                if (this.listSelected) {
                    this.listSelected.forEach(items => {
                        this.list.forEach((item, index) => {
                            if (item.inspectionCode === items.inspectionCode) {
                                this.$nextTick(function() {
                                    this.checked(index)
                                })
                            }
                        })
                    })
                }
            })
        }
    }
}
