import * as api from "@/api/project/srm/dataBase/dictMaintenance"
import { DICT_CODE } from '@/utils/constant'
import caiGou from '@/components/Select/xtpt/caigou'//下拉组件
import throttle from 'lodash/throttle'

export default {
    components: { caiGou },
    data () {
        return {
            dialogStatus: "",
            dialogVisible: false,
            person: "",
            DICT_CODE: DICT_CODE,
            dialogPersonVisible: false,
            parentsRows: [],
            rowId: null,
            temp: {
                purchaseSupporter: undefined,
                parentRowId: undefined,
                platform: true,
            },
            rules: {
            }
        }
    },
    methods: {
        handleClassifyCode () {
            this.$refs.classify.dialogCodeVisible = true
        },
        listSelected (val) {
            delete this.temp.parentName
            this.$set(this.temp, 'parentCode', val[0].parentCode)
            this.$set(this.temp, 'parentName', val[0].parentName)
            this.$refs.classify.dialogCodeVisible = false
        },
        //获取组织编码名称
        selectName (val) {
            this.temp.purchaseOrgName = val
        },
        handleClose () {
            this.$refs.dataForm.resetFields()
        },
        //新增确定按钮单击事件
        createData: throttle(function () {
            this.$refs.dataForm.validate(valid => {
                if (valid) {
                    this.temp.associationName = '数据字典'
                    api.addRecord(this.temp).then(() => {
                        this.$message({
                            title: '成功',
                            message: '新增成功',
                            type: 'success',
                            duration: 2000
                        })
                        this.dialogVisible = false
                        this.$emit('refresh')
                    })
                } else {
                    return false
                }
            })
        }, 2000,
            {
                leading: true,
                trailing: false
            }),
        //编辑确定按钮单击事件
        updateData: throttle(function () {
            api.updateRecord(this.temp.rowId, this.temp).then(() => {
                this.$message({
                    title: '成功',
                    message: '编辑成功',
                    type: 'success',
                    duration: 2000
                })
                this.dialogVisible = false
                this.$emit('refresh')
            })
        }, 2000,
            {
                leading: true,
                trailing: false
            }
        ),
        resetTemp () {
            this.temp = {
                purchaseOrg: undefined,
                parentName: undefined,
                code: undefined,
                name: undefined,
                weight: undefined,
                associationCode: undefined,
                value1: undefined,
                value2: undefined,
                value3: undefined,
                value4: undefined,
                remark: undefined
            }
        }
    }
}
