import { mapState } from "vuex"
import Pagination from '@/components/Pagination' // 分页组件
import { queryRecords } from '@/api/project/srm/materialClassify/orgmaterialParameters'
import { DICT_CODE, TWO_STATE_OPTIONS } from "@/utils/constant"
import dialogMaterielCategory from '@/components/Dialog/dialogMaterielCategory.vue'//主数据物料选择
export default {
    name: 'dialogWlxz',
    components: { Pagination, dialogMaterielCategory },
    //是否多选，名称，是否必须选择
    props: ['unMutiple', 'isRequired', 'title', 'orgCode'],
    data () {
        return {
            valueType: 'code',//code返回code name,row返回一行
            DICT_CODE: DICT_CODE,
            TWO_STATE_OPTIONS: TWO_STATE_OPTIONS,
            listAll: null,//备选项
            listSelected: [],//已选项
            dialogVisible: false,
            totalAll: 0,//总条数
            totalSelected: 0,//选中的条数 暂未使用
            activeName: 'first',//tab激活内容
            tableKey: [0, 1, 2],//表格索引
            currentSelectedRow: null,
            checkProp: ['materialCode', 'materialCode'],
            listQuery: {
                page: true,
                currentPage: 1,
                pageSize: 10,
                materielType: null,
                materielClassify: null,
                materielGroup: null,
                materialCode: null,
                materielName: null
            },//备选项查询条件
            filterConfig: [
                // { type: 'dictSelect', dType: 'dt_materiel_type', label: '物料类型', prop: 'materielType', width: '', class: 'filter-item ml_5' },
                { type: 'dictSelect', dType: 'dt_materiel_classify', label: '物料分类', prop: 'materielClassify', width: '', class: 'filter-item ml_5' },
                // { type: 'dictSelect', dType: 'dt_materiel_group', label: '物料组', prop: 'materielGroup', width: '', class: 'filter-item ml_5' },
                { type: 'commonInput', label: '物料编码', prop: 'materialCode', width: '', class: 'filter-item ml_5' },
                { type: 'commonInput', label: '物料名称', prop: 'materielName', width: '', class: 'filter-item ml_5' },
            ],
            mainTitle: [
                { width: null, align: 'left', label: '物料编码', prop: 'materialCode' },
                { width: '120', align: 'left', label: '物料名称', prop: 'materialName' },
                { width: null, align: 'left', label: '制造区分', prop: 'makeDistinctionCN' },
                { width: null, align: 'left', label: '采购组', prop: 'purchaseGroupName' },
                { width: null, align: 'left', label: '物料分类', prop: 'materialClassificationName' },
                { width: null, align: 'left', label: '采购计量单位', prop: 'purchaseUmoCN' },
            ]//备选项主表头数据
        }
    },
    computed: {
        ...mapState({
            dt_materiel_classify: state => state.dict.dt_materiel_classify,
            dt_materiel_group: state => state.dict.dt_materiel_group,
            dt_materiel_type: state => state.dict.dt_materiel_type,
            dt_manufacture_diff: state => state.dict.dt_manufacture_diff,
            dt_unit_type: state => state.dict.dt_unit_type,
        })
    },
    mounted () {
        this.$store.dispatch('dict/getDicData', ['dt_materiel_classify', 'dt_materiel_group', 'dt_materiel_type', 'dt_manufacture_diff', 'dt_unit_type'])
        this.getList()
    },
    methods: {
        //选中某一行
        checked (i) {
            this.$refs.tbselect.toggleRowSelection(this.listAll[i], true)
        },
        handleQuery () {//查询备选项
            this.getList()
        },
        //初始化选中内容
        initSelect (val) {
            if (this.unMutiple === true && !val) {
                if (!this.listAll) {
                    this.getList()
                }
            } else {
                this.listSelected = val
                this.getList()
            }
        },
        handleClassifyCode () {
            this.$refs.refZsjWlxz.listQuery.orgCode = this.orgCode
            this.$refs.refZsjWlxz.selectedRows = []
            this.$refs.refZsjWlxz.handleQuery()
            this.$refs.refZsjWlxz.dialogVisible = true
        },
        confirmMateriel (data) {
            this.$set(this.listQuery, 'materialClassification', data[0].classCode)
            this.$set(this.listQuery, 'materialClassificationName', data[0].className)
        },
        getList () {//备选项查询
            queryRecords(this.listQuery).then(res => {
                let options = [this.dt_manufacture_diff, this.dt_unit_type]
                res.data.list = this.$myFun.srmCodeToName(res.data.list, options, ['makeDistinction', 'purchaseUmo'])
                this.listAll = res.data.list
                this.totalAll = res.data.pages.count

                if (this.listSelected) {
                    this.listSelected.forEach(items => {
                        this.listAll.forEach((item, index) => {
                            if (item[this.checkProp[0]] === items[this.checkProp[1]]) {
                                this.$nextTick(function () {
                                    this.checked(index)
                                })
                            }
                        })
                    })
                }
            })
        },
        //检查是否取消勾选
        checkUnsel (val, row) {
            let isuncheck = true
            val.forEach(item => {
                if (item.materialCode === row.materialCode) {
                    isuncheck = false
                }
            })
            return isuncheck
        },
        selectMain (val, row) {
            if (this.unMutiple) {
                let isuncheck = this.checkUnsel(val, row)
                if (!isuncheck) {
                    let arr = []
                    arr.push(val[val.length - 1])
                    this.listSelected = arr
                    if (val.length > 1) {
                        this.$refs.tbselect.clearSelection()
                        this.$refs.tbselect.toggleRowSelection(val[val.length - 1], 'selected')
                    }
                } else {
                    this.listSelected = []
                }
            } else {
                let isuncheck = this.checkUnsel(val, row)
                if (!isuncheck) {
                    if (this.listSelected) {
                        val.forEach((items, indexv) => {
                            let isSelected = false
                            this.listSelected.forEach((item) => {
                                if (item.materialCode === items.materialCode) {
                                    isSelected = true
                                }
                            })
                            if (!isSelected) {
                                this.listSelected.push(val[indexv])
                            }
                        })
                    } else {
                        this.listSelected = val
                    }
                } else {
                    this.listSelected = this.listSelected.filter(item => {
                        return item.materialCode !== row.materialCode
                    })
                }
            }
        },
        //供应商选择确定
        dialogConfirm () {
            if (this.isRequired && this.listSelected) {
                if (this.listSelected.length === 0) {
                    this.$message({ type: 'warning', message: '请选择一条数据' })
                } else {
                    this.$emit('handleConfirm', this.listSelected)
                    this.dialogVisible = false
                }
            } else {
                if (this.listSelected) {
                    if (this.listSelected.length === 0) {
                        this.$message({ type: 'warning', message: '请勾选记录' })
                    } else {
                        this.$emit('handleConfirm', this.listSelected)
                        this.dialogVisible = false
                    }
                } else {
                    if (this.valueType === 'code') {
                        this.$emit('handleConfirm', [{ materialCode: null, materielName: null }])
                    } else {
                        this.$emit('handleConfirm', null)
                    }
                    this.dialogVisible = false
                }

            }
        }
    }
}
