import Pagination from '@/components/Pagination' // 分页组件
import * as api from '@/api/project/wms/purchaseReturn/purchaseReturnOrder'
import { mapState } from 'vuex'
import AuthoType from '@/components/Select/AuthoType.vue'//权限类型下拉组件
import gysxz from '@/components/Select/gysxz'//供应商选择
import qrcode from "@/components/QRCode"


export default {
    name: "purchaseReturnOrder",
    components: { Pagination, AuthoType, gysxz, qrcode },
    data () {
        return {
            rheight: 0,
            pheight: 0,
            sheight: 0,
            gysMutiple: true,
            gysValue: '',
            isGysRequired: false,
            options: [],
            options1: [],
            areaList: null,
            locatorList: [],
            list: [],
            list1: null,
            list2: null,
            list3: [],
            list4: [],
            selectItems: [],
            total: 0,
            total1: 0,
            total2: 0,
            total4: 0,
            activeName2: 'first',
            disabled: false,
            unMutiple: false,
            createTime: [this.currentTime(), this.currentTime()],
            createTimeR: [this.currentTime(), this.currentTime()],
            listQuery: {
                page: true,
                currentPage: 1,
                pageSize: 10,
                orgCode: undefined,
                arrivalOrgCode: '',
                businessType: undefined,
                arrivalRepository: undefined,
                accountDate: this.timeFormat(),
                supplierCode: undefined,
            },
            listQuery1: {
                page: true,
                currentPage: 1,
                pageSize: 10,
                orgCode: undefined,
                orderNumber: undefined,
                businessType: undefined,
                warehouseCode: undefined,
                supplierCode: undefined,
            },
            dto: {
                accountDate: undefined,
                businessType: undefined,
                customerCode: undefined,
                orgCode: undefined,
                orgName: undefined,
                outOrderPlans: [],
                sourceOrderCode: undefined,
                warehouseCode: undefined,
            },
            dtos: {
                businessType: undefined,
                orgCode: undefined,
                wareMaterielDTOS: [],
                warehouseCode: undefined,
            },
            orderNumber: undefined,
            locationCode: undefined,
            proofNo: undefined,
            warehouseCode: undefined,
            orgCode: undefined,
            parentsRows: null,
            listQuery2: {
                proofNo: undefined,
                orderNumber: undefined,
                currentPage: 1,
                pageSize: 10,
            },

            printData: {},//打印数据 
            dialogPrintVisible: false,
            value1: '',
            dialogStatus: '',
            selectRowListSchedule: null,
            gysxzDialogFormVisible: false,
            gysxzDialogFormVisible1: false,
            tableKey: [0, 1]
        }
    },
    watch: {
        'listQuery.orgCode': {
            handler (newValue) {
                this.listQuery.orgCode = newValue
                this.listQuery.warehouseCode = ''
                this.listQuery.toWarehouseCode = ''
            },

        },
        'listQuery.warehouseCode': {
            handler (newValue) {
                this.listQuery.warehouseCode = newValue
            },
            deep: true,
            immediate: true
        },
        'listQuery.toWarehouseCode': {
            handler (newValue) {
                this.listQuery.toWarehouseCode = newValue
            },
            deep: true,
            immediate: true
        },
    },
    computed: {
        ...mapState({
            dt_materiel_type: state => state.dict.dt_materiel_type,
            dt_materiel_classify: state => state.dict.dt_materiel_classify,
            dt_purchase_return_type: state => state.businessComponent.dt_purchase_return_type
        })
    },
    mounted () {
        this.setTableHeight()
        this.$store.dispatch('dict/getDicData', ['dt_materiel_type', 'dt_materiel_classify'])
        this.$store.dispatch('businessComponent/getBusinessComponentData', ['dt_purchase_return_type'])
    },
    methods: {
        //表格高度计算
        setTableHeight () {
            this.rheight = this.$myFun.getSingleTbHeight() - 110
            this.pheight = this.$myFun.getSingleTbHeight() + 95
            this.sheight = this.$myFun.getSingleTbHeight() - 35
        },
        timeFormat () {
            var d = new Date()
            var year = d.getFullYear()       //年  
            var month = d.getMonth() + 1     //月  
            var day = d.getDate()            //日  

            var clock = year + "-"
            if (month < 10)
                clock += "0"
            clock += month + "-"

            if (day < 10)
                clock += "0"
            clock += day + " "
            return (clock)
        },
        currentTime () {
            var date = new Date()
            var seperator1 = "-"
            var year = date.getFullYear()
            var month = date.getMonth() + 1
            var strDate = date.getDate()
            if (month >= 1 && month <= 9) {
                month = "0" + month
            }
            if (strDate >= 0 && strDate <= 9) {
                strDate = "0" + strDate
            }
            var currentdate = year + seperator1 + month + seperator1 + strDate
            return currentdate
        },
        setdefault (val, i) {
            if (i === 1) {
                this.listQuery.orgCode = val
            } else if (i === 3) {
                this.listQuery.warehouseCode = val
                this.listQuery.businessType = this.dt_purchase_return_type.options[0].wareRuleCode
            }
        },
        setdefault1 (val, i) {
            if (i === 1) {
                this.listQuery1.orgCode = val
            } else if (i === 3) {
                this.listQuery1.warehouseCode = val
                this.listQuery1.businessType = this.dt_purchase_return_type.options[0].wareRuleCode
            }
        },
        changeOrg () {//组织编码下拉change
            this.listQuery1.orgCode = this.listQuery.orgCode
        },
        changeToWa () {//仓库编码下拉change
            this.listQuery1.warehouseCode = this.listQuery.warehouseCode
        },
        changeBusinessType () {//业务类型下拉change
            this.listQuery1.dicType = this.listQuery.businessType
        },
        getList () {//查询
            // const createTime = this.listQuery.createTime
            if (this.createTimeR) {
                this.listQuery.createDateStart = this.createTimeR[0]
                this.listQuery.createDateEnd = this.createTimeR[1]
            } else {
                this.listQuery.createDateStart = ""
                this.listQuery.createDateEnd = ""

            }
            api.queryRecords(this.listQuery).then(res => {
                this.list = res.data.list
                this.total = res.data.pages.count
            })
        },
        getList2 () {//从表查询
            api.queryRecordsmx(this.listQuery, this.selectlistRow.orderNumber).then(res => {
                res.data.list.forEach((item) => {
                    item.retentionQuantity = item.stockQuantity - item.returnQuantity
                })
                this.list2 = res.data.list
                this.total2 = res.data.pages.count
            })
        },
        getList3 () {
            if (this.createTime) {
                this.listQuery1.createDateStart = this.createTime[0]
                this.listQuery1.createDateEnd = this.createTime[1]
            } else {
                this.listQuery1.createDateStart = ""
                this.listQuery1.createDateEnd = ""
            }
            api.query(this.listQuery1).then(res => {
                this.list3 = res.data.list
            })
        },
        getList4 () {
            api.queryDetails(this.listQuery1, this.selectlistRowchange.outOrderNumber).then(res => {
                this.list4 = res.data.list
                this.total4 = res.data.pages.count
            })
        },
        //查询
        handleQuery () {
            if (this.listQuery.orgCode && this.listQuery.warehouseCode && this.listQuery.businessType && this.listQuery.accountDate) {
                this.listQuery.currentPage = 1
                this.getList()
            } else {
                this.$message({
                    title: this.$t("message.warning"),
                    message: this.$t("message.selectQueryConditions"),
                    type: "warning"
                })
            }
            api.getArea(this.listQuery.warehouseCode).then(resa => {
                this.areaList = resa.data
            })
        },
        showGys (val) {
            this.gysValue = val
            this.$refs.dialogRefgys.dialogVisible = true
        },
        //供应商确认
        handleGysClick (gysRows) {
            if (this.gysValue === 'register') {
                this.parentsRows = gysRows
                if (!this.parentsRows[0]) {
                    this.$set(this.listQuery, 'supplierCode', "")
                } else {
                    this.$set(this.listQuery, 'supplierCode', this.parentsRows[0].supplierCode)
                }
            } else if (this.gysValue === 'proof') {
                this.parentsRows = gysRows
                if (!this.parentsRows[0]) {
                    this.$set(this.listQuery1, 'supplierCode', "")
                } else {
                    this.$set(this.listQuery1, 'supplierCode', this.parentsRows[0].supplierCode)
                }
            }
        },
        selectRow (val) {
            this.selectRowData = val
            this.$refs.tb.clearSelection()
            this.$refs.tb.toggleRowSelection(val)
        },
        selectRow1 (val) {//表一复选框选中方法
            this.selectlistRow = val[val.length - 1]
            this.warehouseCode = val.warehouseCode
            if (val.length > 1) {
                this.$refs.tb.clearSelection()
                this.$refs.tb.toggleRowSelection(val[val.length - 1])
            }
            this.$refs.tb.setCurrentRow(this.selectlistRow)
            if (this.selectlistRow) {
                this.getList2()
            }
        },
        handleUpdate () {//保存
            let result = true
            let status = false
            let creatList = []
            this.dto.orgCode = this.listQuery.orgCode
            this.dto.orgName = this.selectlistRow.orgName
            this.dto.businessType = this.listQuery.businessType
            this.dto.sourceOrderCode = this.selectlistRow.orderNumber
            this.dto.customerCode = this.selectlistRow.supplierCode
            this.dto.accountDate = this.listQuery.accountDate
            this.dto.warehouseCode = this.listQuery.warehouseCode
            this.dto.preSourceReceiptCode = this.selectlistRow.orderNumber
            this.list2.forEach((item) => {
                this.list2.map(v => {
                    v.measuringUnit = v.measuringUnitName,
                        v.outStockNum = v.deliveredQuantity,
                        v.sourceDetailNo = v.rowNo
                    v.toWareAreaCode = v.toAreaCode
                    v.toWareLocationCode = v.toLocationCode
                    return v
                })
                if (item.outStockNum > 0) {
                    creatList.push(item)
                    result = false
                    if (item.deliveredQuantity > item.retentionQuantity) {
                        status = true
                        // this.$message({
                        //     //'本次退货数”应小于等于“可退数”'
                        //     title: this.$t("message.warning"),
                        //     message: this.$t("message.numberOfReturnsShouldBeLessThanTheNumberOfReturnsTthatCanBeMade"),
                        //     type: "warning"
                        // })
                        // return
                    }
                    this.dto.outOrderPlans = creatList
                }
            })
            if (status) {
                this.$message({
                    //'本次退货数”应小于等于“可退数”'
                    title: this.$t("message.warning"),
                    message: this.$t("message.numberOfReturnsShouldBeLessThanTheNumberOfReturnsTthatCanBeMade"),
                    type: "warning"
                })
            }
            else if (result) {
                this.$message({
                    //'本次发货不可全部为0或空'
                    title: this.$t("message.warning"),
                    message: this.$t("message.deliveryCannotBeZeroOrEmpty"),
                    type: "warning"
                })
            } else {
                api.addRecords(this.dto).then(() => {
                    this.$message({
                        title: this.$t("message.success"),
                        message: this.$t("message.saveSuccessfully"),
                        type: 'success',
                        duration: 2000
                    })
                    this.getList2()
                })
            }
        },
        handleRecommend () {
            if (this.selectRowListSchedule) {
                this.dtos.businessType = this.listQuery.businessType,
                    this.dtos.orgCode = this.listQuery.orgCode,
                    this.selectRowListSchedule.materielClassify = ""
                this.dtos.wareMaterielDTOS = this.selectRowListSchedule
                this.dtos.warehouseCode = this.listQuery.warehouseCode,
                    api.recommend(this.dtos).then(res => {
                        res.forEach((val) => {
                            this.list2.forEach((val1, index) => {
                                if (val.materielCode == val1.materielCode) {
                                    this.$set(this.list2[index], 'toAreaCode', val.areaCode)
                                    this.$set(this.list2[index], 'toLocationCode', val.locationCode)
                                    this.$set(this.list2, index, this.list2[index])
                                }
                            })
                        })
                    })
            } else {
                this.$message({
                    title: this.$t("message.warning"),
                    message: this.$t("message.checkeMessage"),
                    type: "warning"
                })
            }
        },
        selectRowSchedule (val) {
            this.selectRowListSchedule = val
        },
        handleCopy () {//复制
            if (this.selectRowListSchedule.length) {
                this.list2.map((item, index) => {
                    this.selectRowListSchedule.forEach(items => {
                        if (items.materielCode === item.materielCode) {
                            this.list2[index].deliveredQuantity = item.stockQuantity
                            this.$set(this.list2, index, this.list2[index])
                        }
                    })
                })
            } else {
                this.$message({
                    title: this.$t("message.warning"),
                    message: this.$t("message.checkedplease"),
                    type: "warning"
                })
            }

        },
        handleFilter () {
            if (this.listQuery1.businessType) {
                this.getList3()
            } else {
                this.$message({
                    title: this.$t("message.warning"),
                    message: this.$t("message.selectQueryConditions"),
                    type: "warning"
                })
            }

        },

        getLocatorList (warehouseCode, areaCode, i) {
            api.getLocator(warehouseCode, areaCode).then(res => {
                this.$set(this.locatorList, i, res.data)
                this.$set(this.list2[i], 'toLocationCode', res.data[i].locationCode)
                this.$set(this.list2, i, this.list2[i])

            })
        },
        changeAreaSel (areaCode, i) {
            this.getLocatorList(this.listQuery.warehouseCode, areaCode, i)
        },
        //导出
        handleDownload () {
            this.downloadLoading = true
            import('@/vendor/Export2Excel').then(excel => {
                const tHeader = [
                    '凭证号', '业务类型', '物料编码', '物料名称',
                    '数量', '供应商编码', '供应商名称', '组织编码',
                    '仓库编码', '区位', '储位', '记账日期',
                    '创建日期', '记账人', '订单号', '订单行号',
                ]
                const filterVal = [
                    'outOrderNumber', 'businessType', 'materielCode', 'materielName',
                    'outStockNum', 'customerCode', 'customerName', 'orgCode',
                    'warehouseCode', 'wareAreaCode', 'wareLocationCode', 'accountDate',
                    'createTime', 'creator', 'sourceOrderCode', 'rowNo',
                ]
                const data = this.formatJson(filterVal, this.list4)
                excel.export_json_to_excel({
                    header: tHeader,
                    data,
                    filename: 'table-list'
                })
                this.downloadLoading = false
            })
        },
        formatJson (filterVal, jsonData) {
            return jsonData.map(v => filterVal.map(j => {
                if (j === 'timestamp') {
                    return
                } else {
                    return v[j]
                }
            }))

        },
        selectRowchange (val) {
            this.selectlistRowchange = val[val.length - 1]
            if (val.length > 1) {
                this.$refs.tbb.clearSelection()
                this.$refs.tbb.toggleRowSelection(val.pop())
            }
            this.$refs.tbb.setCurrentRow(this.selectlistRowchange)
            if (this.selectlistRowchange) {
                this.getList4()
            }
        },
        clickProofNo (val) {
            this.clicklistProofNo = val
            this.$refs.tbb.clearSelection()
            this.$refs.tbb.toggleRowSelection(val) //单击行绑定点击事件
        },
        //打印
        receiptPrint () {
            if (this.selectlistRowchange) {
                this.dialogPrintVisible = true
                this.dialogStatus = "print"
                this.printData = this.selectlistRowchange
                this.$nextTick(() => {
                    this.$refs.childQRcode.qrCreate(this.printData.outOrderNumber)
                })
            } else {
                this.$message.warning(this.$t('message.checkedplease'))
                return false
            }
        },
        //打印执行
        doReceiptPrint () {
            let newWin = window.open("")
            var newstr = document.getElementById('receipt').innerHTML
            newWin.document.write(newstr)
            newWin.document.close()//IE添加

            setTimeout(function () {
                newWin.print()
                newWin.close()
            }, 100)

        },
    }
};
