import gysxz from '@/components/Select/xtpt/gysxz'//供应商选择
import wlxzbyzsj from '@/components/Select/wlxzbyzsj.vue'//物料主数据
import contentProof from "./content/contentProof.vue"
import contentRegister from "./content/contentRegister.vue"


export default {
    name: "noIndicatorStorage",
    components: { contentProof, contentRegister, gysxz, wlxzbyzsj },
    data () {
        return {
            activeContent: 'content1',
            isGysRequired: false,//供应商非必选
            gysMutiple: true,//供应商单选
            gysValue: null,//供应商赋值条件
            wlValue: null,
            wlxzDialogFormVisible: false
        }
    },
    methods: {
        //供应商显示
        showGys (val) {
            this.gysValue = val
            this.$refs.dialogRefgys.dialogVisible = true
        },
        //供应商确认
        handleGysClick (gysRows) {
            if (this.gysValue === 'register') {
                this.$refs.cregister.setGys(gysRows)
            } else if (this.gysValue === 'proof') {
                this.$refs.cproof.setGys(gysRows)
            }
        },
        showWlxz (val) {
            if (!val) {
                this.wlxzDialogFormVisible = false
            } else {
                this.wlValue = val
                this.wlxzDialogFormVisible = true
                this.$nextTick(() => {
                    this.$refs.wlxzRef.listSelected = [...this.$refs.cregister.list_a]
                    this.$refs.wlxzRef.getList()
                })
            }
        },
        handleWlxzClick () {
            if (this.wlValue === 'register') {
                this.$refs.cregister.setWlxz(this.$refs.wlxzRef.listSelected)
            }
        },
        getData () {
            this.$refs.cproof.list_b_1 = []
            let dto = {
                orgCode: undefined
            }
            dto = this.$refs.cregister.listQuery_a
            this.$refs.cproof.getList_b(dto)
        }
    }
};
