    name: "detailQuoteNote",
    components: { caiGou, Pagination, addMain },
    data () {
        return {
            rangeDate: null,
            theight: 0,//表格高度
            isSingle: false,//表格是否单选 点击各按钮根据流程逻辑控制单多选
            dataList: [],
            total: 0,
            listQuery: {
                //查询
                page: true,
                currentPage: 1,
                pageSize: 15,
                orgCode: null,
