            DICT_CODE: DICT_CODE,
            currentSelectedRow: null,//当前选中的行
            selectedRows: [],//多选时选中的所有行
            tableKey: 0, //表格索引
        }
    },
    created () { },
    computed: {
        ...mapState({
            dt_srm_p0: state => state.dict.dt_srm_p0,
            dt_srm_pdj: state => state.dict.dt_srm_pdj,
            dt_srm_pdi: state => state.dict.dt_srm_pdi,
        })
    },
    filters: {
        stateFilter: function (a) {
            if (a === "0") {
                return "未报价"
            } else {
                return "已报价"
            }
        }
