        //表格高度自适应
        window.onresize = () => {
            this.setTableHeight()
        }
        this.getList()
    },
    watch: {
        'rangeDate' (value) {
            if (value) {
                this.listQuery.createDateBegin = value[0]
                this.listQuery.createDateEnd = value[1]
            } else {
                this.listQuery.createDateBegin = undefined
                this.listQuery.createDateEnd = undefined
            }
        }
    },
    methods: {

