        },
        refresh () {
            this.getList()
        },
        //获取表格数据
        getList () {
            this.dataList = [
                { params1: '1', params2: '2019', params3: 'PV采购组织', params4: '武汉工厂', params5: '传动轴', params6: 'X6_QC2018051601', params7: '01', params8: 'X6_QC2018051601', params9: '1000', params10: '生效', params11: '黄渤', params12: '2019-10-22 11:28:36', params13: '黄渤', params14: '2019-10-22 11:28:36' }
