        handleSizeChange (val) {
            this.listQuery.pageSize = val
            this.getList()
        },
        //表格当前页变化
        handleCurrentChange (val) {
            this.listQuery.currentPage = val
            this.getList()
        },
        //新增按钮
        handleCreate () {
            this.$refs.addMain.dialogStatus = 'create'
            this.$refs.addMain.resetTemp()
            this.$refs.addMain.dialogVisible = true
        },
        //表格操作编辑
        handleUpdate (index, row) {
            this.$refs.addMain.temp = { ...row }
            this.$refs.addMain.dialogVisible = true
        },
        //模板下载按钮
        handleModelDownload () {
            console.log('模板下载')
        },
        //导入按钮
        handleImport () {
            console.log('导入')
            this.$refs.refImport.getList()
            this.$refs.refImport.dialogVisible = true
        },
        //发布按钮
        handlePublish () {
            if (this.selectedRows && this.selectedRows.length > 0) {
                this.$confirm('是否提交该条信息?', '提示', {
                    confirmButtonText: '确定',
                    cancelButtonText: '取消',
                    type: 'warning'
                }).then(() => {
