                    //     this.$message({
                    //         title: '成功',
                    //         message: '发布成功',
                    //         type: 'success',
                    //         duration: 2000
                    //     })
                    //     this.getList()
                    // })
                }).catch(() => {
                    this.$message({
                        type: 'info',
                        message: '已取消'
                    })
                })
            } else {
                this.$message({
                    type: 'warning',
                    message: '请先选择信息'
                })
            }

        },
        //表格操作删除
        handleDelete (index, row) {
            console.log(index, row)
            this.$confirm('确认要删除该数据项吗?', '提示', {
                confirmButtonText: '确定',
                cancelButtonText: '取消',
                type: 'warning'
            }).then(() => {
                // api.deleteRecord(row.rowId).then(() => {
                //     this.$message({
                //         type: 'success',
                //         message: '删除成功'
                //     })
                //     this.getList()
                // })
            }).catch(() => {
                this.$message({
                    type: 'info',
                    message: '已取消'
                })
            })
        },
        //表格操作预览
        handlePreview (index, row) {
            console.log('预览', index, row)
        },
        // 表格选择框选中 注:参数为选中的所有行的数组
        selectChange (val) {
            if (this.isSingle) {
                if (val.length > 1) {
                    this.$refs.tb.clearSelection() //清除其他行的选中
                    this.$refs.tb.toggleRowSelection(val[val.length - 1], "selected") //单击行绑定点击事件
                } else if (val.length === 1) {
                    this.selectedRows = val
                    this.currentSelectedRow = val[val.length - 1]
                } else if (val.length === 0) {
                    this.selectedRows = val
                    this.currentSelectedRow = null
                }
            } else {
                this.selectedRows = val
                this.currentSelectedRow = val[val.length - 1]
            }
        },
        //点击表格某一行
        rowClick (val) {
            this.$refs.tb.toggleRowSelection(val, "selected") //单击行绑定点击事件
        }
    }
};
