import { queryCurve } from "@/api/project/iot/IOT/db"
import { getDeviceDate } from "@/api/project/iot/MOM/deviceQuery"
export default {
    name: "DBTimeData",
    props: {
        row: {
            type: Object,
            default: undefined
        },
        code: {
            type: String,
            default: undefined
        }
    },
    data () {
        return {
            listLoading: false,
            theight: 0,//表格高度
            list: [],
            tableKey: 0,//表格索引
            listQuery: {
                pointEndTime: undefined,
                pointStartTime: undefined
            },
            dialogFormVisible: false,
            pointCode: undefined,
            chartList: null
        }
    },
    mounted () {
        this.setTableHeight()
        //表格高度自适应
        window.onresize = () => {
            this.setTableHeight()
        }
        this.getList()
    },
    methods: {
        //获取表格数据
        getList () {
            this.listLoading = true
            getDeviceDate(this.code, this.row.gatewayCode, this.row.deviceCode).then(response => {
                this.list = response.data
                setTimeout(() => {
                    this.listLoading = false
                }, 1.5 * 100)
            })
        },
        // 查看曲线
        handleViewCurves (pointCode) {
            this.pointCode = pointCode
            this.dialogFormVisible = true
            this.handleQuery()
        },
        // 搜索曲线
        handleQuery () {
            queryCurve(this.code, this.row.gatewayCode, this.row.deviceCode, this.pointCode, this.listQuery).then(res => {
                this.chartList = res.data
                this.drawLine()
            })
        },
        drawLine () {
            let xAxisData = this.chartList.map(item => {
                return item.pointTime
            })
            let data = this.chartList.map(item => {
                return {
                    value: item.pointValue,
                    name: item.pointName,
                }
            })
            // 基于准备好的dom，初始化echarts实例
            let myChart = this.$echarts.init(this.$refs.myChart)
            // 绘制图表
            myChart.setOption({
                title: { text: '' },
                tooltip: {
                    formatter: '{b}<br/>{c}'
                },
                xAxis: {
                    data: xAxisData
                },
                yAxis: {},
                series: [
                    {
                        name: '',
                        type: 'line',
                        data: data
                    }
                ]
            })
        },
        //表格高度计算
        setTableHeight () {
            this.theight = this.$myFun.getSingleTbHeight() - 30
        },
    }
};
