import * as api from "@/api/project/srm/priceManage/priceConfirmList"
import { DICT_CODE } from '@/utils/constant'
import dialogGysxz from '@/components/Dialog/dialogsGysxz'//供应商选择
import { mapState } from "vuex"
import throttle from 'lodash/throttle'

export default {
    components: { dialogGysxz },
    data () {
        return {
            DICT_CODE: DICT_CODE,
            dialogVisible: false,
            dialogStatus: '',
            tableKey: 0,
            list: [],
            temp: {
                evaluationNo: undefined,
                evaluationItem: undefined,
                supplierCode: undefined,
                supplierName: undefined,
                scoringMethod: undefined,
                evaluationItems: undefined,
                evaluationDetails: undefined,
                divide: undefined,
                weight: undefined,
            },
            rules: {
                scoringMethod: [{ required: true, message: '评分方式不能为空', trigger: 'blur' }],
                evaluationItems: [{ required: true, message: '评价项不能为空', trigger: 'blur' }],
                evaluationDetails: [{ required: true, message: '评价明细不能为空', trigger: 'blur' }],
                divide: [{ required: true, message: '除分不能为空', trigger: 'blur' }],
                weight: [{ required: true, message: '权重不能为空', trigger: 'blur' }],
            }
        }
    },
    computed: {
        ...mapState({
            dt_srm_pb4: state => state.dict.dt_srm_pb4,  // 评价项
            dt_srm_pb5: state => state.dict.dt_srm_pb5,  // 评分方式

        })
    },
    mounted () {
        this.$store.dispatch("dict/getDicData", ["dt_srm_pb4", 'dt_srm_pb5'])
    },
    methods: {
        //获取采购组织名称
        selectName (val) {
            this.temp.orgName = val
        },
        handleClose () {
            this.$refs.dataForm.resetFields()
        },
        initData () {

        },
        //供应商
        showGys () {
            this.$refs.refGys.dialogVisible = true
            this.$refs.refGys.getList()
        },
        //供应商确定
        handleGysClick (val) {
            this.$set(this.temp, 'supplierCode', val[0].supplierCode)
            this.$set(this.temp, 'supplierName', val[0].supplierName)
        },
        createData: throttle(function () {
            this.$refs.dataForm.validate(valid => {
                if (valid) {
                    let status = this.isRepeat(this.list)
                    console.log(status, 'status')
                    if (status) {
                        this.$message({
                            title: "警告",
                            message: "物料编码不能重复",
                            type: "warning"
                        })
                    } else {
                        let dto = {
                            evaluationNo: this.temp.evaluationNo,
                            evaluationItem: this.temp.evaluationItem,
                            supplierCode: this.temp.supplierCode,
                            supplierName: this.temp.supplierName,
                            scoringMethod: this.temp.scoringMethod,
                            evaluationItems: this.temp.evaluationItems,
                            evaluationDetails: this.temp.evaluationDetails,
                            divide: this.temp.divide,
                            weight: this.temp.weight,
                        }
                        api.createRecords(dto).then(() => {
                            this.$message({
                                title: '成功',
                                message: '新增成功',
                                type: 'success',
                                duration: 2000
                            })
                            this.dialogVisible = false
                            this.$emit('refresh')
                        })
                    }

                } else {
                    return false
                }
            })
        }, 2000,
            {
                leading: true,
                trailing: false
            }),
        updateData: throttle(function () {
            let status = this.isRepeat(this.list)
            if (status) {
                this.$message({
                    title: "警告",
                    message: "物料编码不能重复",
                    type: "warning"
                })
            } else {
                let dto = {
                    evaluationNo: this.temp.evaluationNo,
                    evaluationItem: this.temp.evaluationItem,
                    supplierCode: this.temp.supplierCode,
                    supplierName: this.temp.supplierName,
                    scoringMethod: this.temp.scoringMethod,
                    evaluationItems: this.temp.evaluationItems,
                    evaluationDetails: this.temp.evaluationDetails,
                    divide: this.temp.divide,
                    weight: this.temp.weight,
                }
                api.updateRecords(this.temp.rowId, dto).then(() => {
                    this.$message({
                        title: '成功',
                        message: '编辑成功',
                        type: 'success',
                        duration: 2000
                    })
                    this.dialogVisible = false
                    this.$emit('refresh')
                })
            }
        }, 2000,
            {
                leading: true,
                trailing: false
            })
    }
}
