import * as api from '@/api/project/srm/contractManage/dailyEvaluate'
import { queryRecords } from "@/api/project/srm/dataBase/dictMaintenance"
import { DICT_CODE } from '@/utils/constant'
import caiGou from '@/components/Select/xtpt/caigou'//下拉组件
import Pagination from "@/components/Pagination"
import addMain from "./dialog/addMain"
import { mapState } from "vuex"

export default {
    name: "dailyEvaluate",
    components: { Pagination, caiGou, addMain },
    data () {
        return {
            phaseList: [],
            modalnum: null,//模板编号，非模板页面可删
            theight: 0,//表格高度
            isSingle: true,//表格是否单选 点击各按钮根据流程逻辑控制单多选
            list: [],
            total: 0,
            listQuery: {
                //查询
                page: true,
                currentPage: 1,
                pageSize: 15,
                orgCode: 'dtcg0001'
            },
            DICT_CODE: DICT_CODE,
            currentSelectedRow: null,//当前选中的行
            selectedRows: null,//多选时选中的所有行
            tableKey: 0,//表格索引
            tableConfigSub: [
                { label: '状态', prop: 'stateCN', align: 'center', width: 'auto' },
                { label: '评价类型', prop: 'evaluationTypeCN', align: 'center', width: 'auto' },
                { label: '评价阶段', prop: 'evaluationStageCN', align: 'center', width: 'auto' },
                { label: '年度', prop: 'evaluationYear', align: 'center', width: 'auto' },
                { label: '供应商', prop: 'supplierCode', align: 'center', width: 'auto' },
                { label: '供应商名称', prop: 'supplierName', align: 'center', width: 'auto' },
                { label: '评价项', prop: 'evaluationItemCN', align: 'center', width: 'auto' },
                { label: '得分', prop: 'score', align: 'center', width: 'auto' },
                { label: '等级', prop: 'gradeCN', align: 'center', width: 'auto' },
                { label: '评价', prop: 'remark', align: 'center', width: 'auto' },
                { label: '评价人', prop: 'evaluationUser', align: 'center', width: 'auto' },
                { label: '提交日期', prop: 'submitDate', align: 'center', width: 'auto' },
                { label: '组织编码', prop: 'orgCode', align: 'center', width: 'auto' },
                { label: '评价单号', prop: 'evaluationNo', align: 'center', width: 'auto' },
                { label: '行号', prop: 'rowId', align: 'center', width: 'auto' },
                { label: '评价计划号', prop: 'evaluationPlanNo', align: 'center', width: 'auto' },
                { label: '评价模型版本', prop: 'version', align: 'center', width: 'auto' },
            ],
            index: 0
        }
    },
    created () { },
    computed: {
        ...mapState({
            dt_srm_p0: state => state.dict.dt_srm_p0,    // (单据)状态
            dt_srm_pal: state => state.dict.dt_srm_pal,  // 评价状态
            dt_srm_pam: state => state.dict.dt_srm_pam,  // 评价等级
            dt_srm_pak: state => state.dict.dt_srm_pak,  // 评价类型
            dt_srm_pb2: state => state.dict.dt_srm_pb2,  // 评价类别
            dt_srm_pb3: state => state.dict.dt_srm_pb3,  // 评价阶段
            dt_srm_pb4: state => state.dict.dt_srm_pb4   // 评价类（项）
        })
    },
    mounted () {
        this.$store.dispatch("dict/getDicData", ['dt_srm_p0', 'dt_srm_pal', 'dt_srm_pam', 'dt_srm_pak', 'dt_srm_pb2', 'dt_srm_pb3', 'dt_srm_pb4'])
        this.setTableHeight()
        //表格高度自适应
        window.onresize = () => {
            this.setTableHeight()
        }
        this.getList()
    },
    methods: {
        //评价类别评价阶段联动
        getPhaseData (val) {
            if (val) {
                let dto = {
                    currentPage: 1,
                    pageSize: 15,
                    parentCode: val
                }
                queryRecords(dto).then(res => {
                    this.phaseList = res.data.list
                })
            }
        },
        //表格高度计算
        setTableHeight () {
            this.theight = this.$myFun.getSingleTbHeight()
        },
        //获取组织编码名称
        selectName (val) {
            this.listQuery.orgName = val
        },
        //获取表格数据
        getList () {
            api.queryRecord(this.listQuery).then((res) => {
                let options = [this.dt_srm_p0, this.dt_srm_pak, this.dt_srm_pam, this.dt_srm_pb3, this.dt_srm_pb4, this.dt_srm_pal]
                this.list = this.$myFun.srmCodeToName(res.data.list, options, ['state', 'evaluationType', 'grade', 'evaluationStage', 'evaluationItem', 'state'])
                // console.log(res, '1')
            })
        },
        //表格查询
        handleQuery () {
            this.listQuery.currentPage = 1
            this.getList()
        },
        //提交按钮单击事件
        handleSubmit () {
            if (!this.currentSelectedRow) {
                this.$message({
                    type: 'warning',
                    message: '请选择一条信息！'
                })
            } else {
                // console.log(this.currentSelectedRow)
                let dto = {
                    method: "SUBMIT",
                    gradeItemDTO: {
                        evaluationNo: this.currentSelectedRow.evaluationNo,
                        rowId: this.currentSelectedRow.rowId,
                    }

                }
                api.submitRecord(this.currentSelectedRow.evaluationNo, this.currentSelectedRow.rowId, dto).then(() => {
                    this.$message({
                        title: '成功',
                        type: 'success',
                        message: '提交成功！',
                        duration: 1000
                    })
                    this.getList()
                })
            }
        },
        //行内评按钮单击事件
        handleEvaluate (index, val) {
            this.$refs.addMain.dialogVisible = true
            this.$refs.addMain.temp = { ...val }
            this.$refs.addMain.type = 'grade'
            // 记录选中这行的index
            this.index = index
        },
        // 评价确认
        handleSave (temp) {
            console.log(temp, 'temp', this.index)
            this.list.splice(this.index, 1, temp)
            this.getList()
        },
        //行内查询按钮单击事件
        handleSearch (index, val) {
            this.$refs.addMain.dialogVisible = true
            this.$refs.addMain.temp = { ...val }
            this.$refs.addMain.type = 'see'
        },
        // 表格选择框选中 注:参数为选中的所有行的数组
        selectChange (val) {
            if (this.isSingle) {
                if (val.length > 1) {
                    this.$refs.tb.clearSelection() //清除其他行的选中
                    this.$refs.tb.toggleRowSelection(val[val.length - 1], "selected") //单击行绑定点击事件
                } else if (val.length === 1) {
                    this.selectedRows = val
                    this.currentSelectedRow = val[val.length - 1]
                }
            } else {
                this.selectedRows = val
                this.currentSelectedRow = val[val.length - 1]
            }
        },
        //点击表格某一行
        rowClick (val) {
            this.$refs.tb.toggleRowSelection(val, "selected") //单击行绑定点击事件
        }
    }
};
