export default {
    name: 'contentBasic',
    data () {
        return {
            showAll: false,//根据点击的type是否显示
            formList: {
                activityid: '',
                name: '',
                datastate: '',
                flowstate: '',
                actstarted: '',
                actcompleted: '',
                beftasking: '',
                timeauto: '',
                taskcreated: '',
                taskcompleted: '',
                isSendMessage: false,
                messagetypeArr: [],
                messagetype: '',
                isAutoreminder: '',
                autoreminder: '',
                webroot: '',
                assigneetype: '',
                claim: false,
                countersign: false,
                carboncopy: false,
                percent: '',
                rollbacktype: '',
                desctype: false,
                autonode: false,
                samepauto: false,
                discusstag: false,
                rolebackhi: false,
                exclusivetype: null
            },
            dataStateArr: [
                { key: '设计', value: 0 },
                { key: '编制', value: 1 },
                { key: '提交', value: 2 },
                { key: '驳回', value: 3 },
                { key: '审批', value: 4 },
                { key: '签收', value: 5 },
                { key: '发布', value: 6 },
                { key: '批转', value: 7 },
                { key: '作废', value: 8 },
                { key: '终止', value: 9 }
            ],//数据状态
            activeCollNames: ['1', '2'],
            nodeDataList: []
        }
    },
    computed: {},
    mounted () {

    },
    methods: {
        //点击节点时初始化表单
        initForm (data) {
            for (let key in this.formList) {
                for (let keym in data) {
                    for (let keyact in data[keym]) {
                        if (keyact === key) {
                            if (key === 'messagetype') {
                                this.isSendMessage = true
                                this.$set(this.formList, 'messagetype', data[keym][keyact])
                                if (data[keym][keyact]) {
                                    this.$set(this.formList, 'messagetypeArr', data[keym][keyact].split(','))
                                } else {
                                    this.$set(this.formList, 'messagetypeArr', [])
                                }
                            } else {
                                this.$set(this.formList, key, data[keym][keyact])
                            }
                        }
                    }
                }
            }
            this.initCheckVal()
        },
        //是否发送消息变化
        changeMessage (val) {
            console.log(val)
        },
        //messageType赋值
        setMt () {
            this.$set(this.formList, 'messagetype', this.formList.messagetypeArr.join(','))
        },
        //消息循环变化
        changeReminder (val) {
            if (val) {
                this.$set(this.formList, 'isSendMessage', true)
            }
        },
        //驳回配置变化
        changeRollback (val) {
            console.log(val)
        },
        //获取指定节点下拉框内容
        getNodeList (data, nodeDataList) {
            this.$set(this.formList, 'activityid', data.activityId)
            this.$set(this.formList, 'name', data.name)
            this.nodeDataList = nodeDataList
        },

        //类型配置变化
        changeAssigneetype (val) {
            console.log(val)
            this.$emit('changeAssigneetype', val)
            if (val == 0) {
                this.$set(this.formList, 'exclusivetype', null)
            }
        },
        //获取值转checkbox的值
        transCheckVal (val) {
            if (val === 1 || val === 'true' || val === '1') {
                return true
            } else {
                return false
            }
        },
        initCheckVal () {
            this.$set(this.formList, 'claim', this.transCheckVal(this.formList.claim))
            this.$set(this.formList, 'countersign', this.transCheckVal(this.formList.countersign))
            this.$set(this.formList, 'carboncopy', this.transCheckVal(this.formList.carboncopy))
            this.$set(this.formList, 'desctype', this.transCheckVal(this.formList.desctype))
            this.$set(this.formList, 'autonode', this.transCheckVal(this.formList.autonode))
            this.$set(this.formList, 'samepauto', this.transCheckVal(this.formList.samepauto))
            this.$set(this.formList, 'discusstag', this.transCheckVal(this.formList.discusstag))
            this.$set(this.formList, 'rolebackhi', this.transCheckVal(this.formList.rolebackhi))
        },
        //checkbox的值转上传值
        transValCheck (val) {
            if (!val) {
                return 0
            } else if (val === true) {
                return 1
            }
        },
        setCheckVal () {
            this.$set(this.formList, 'claim', this.transValCheck(this.formList.claim))
            this.$set(this.formList, 'countersign', this.transValCheck(this.formList.countersign))
            this.$set(this.formList, 'carboncopy', this.transValCheck(this.formList.carboncopy))
            this.$set(this.formList, 'desctype', this.transValCheck(this.formList.desctype))
            this.$set(this.formList, 'autonode', this.transValCheck(this.formList.autonode))
            this.$set(this.formList, 'samepauto', this.transValCheck(this.formList.samepauto))
            this.$set(this.formList, 'discusstag', this.transValCheck(this.formList.discusstag))
            this.$set(this.formList, 'rolebackhi', this.transValCheck(this.formList.rolebackhi))
        }
    }
}
