import Pagination from "@/components/Pagination" // 分页组件
import *  as api from '@/api/project/eas/database/pointPreparation'

export default {
    name: "pointSelection",
    components: { Pagination },
    data () {
        return {
            rheight: 0,
            pheight: 0,
            sheight: 0,
            options: [],
            list: [],
            subList: null,
            total: 0,
            activeName2: "first",
            listQuery: {
                currentPage: 1,
                pageSize: 15,
                inspectionName: undefined,
                typeCode: undefined,
            },
            tableKey: [0, 1],
        }
    },

    mounted () {
        this.setTableHeight()
        this.getList()
        this.getTypeCode()
    },
    watch: {
        // 监听时间区间数组 写入到对应字段
        subList (value) {
            this.$parent.$parent.listSub = value
        }
    },
    methods: {
        //表格高度计算
        setTableHeight () {
            this.rheight = this.$myFun.getSingleTbHeight() - 30
            this.pheight = this.$myFun.getSingleTbHeight() + 55
            this.sheight = this.$myFun.getSingleTbHeight() - 70
        },
        //查询设备类型
        getTypeCode () {
            api.queryTypeCode().then(res => {
                this.options = res.data
            })
        },

        // 父级工位表格展示已选，modal已选勾选
        getListSelected () {
            this.subList = this.$parent.$parent.listSub
        },
        handleQuery () {
            //查询
            this.listQuery.currentPage = 1
            this.getList()
            this.subList = []
        },
        selectRow (val) {
            this.selectRowData = val
            this.$emit("change", this.selectRowData)
            // 有选中检查是否已选
            for (let i = 0; i < this.subList.length; i++) {
                if (this.subList[i].inspectionCode == val.inspectionCode) {
                    // 在已选中去除该条
                    this.popSelected(val)
                    return false
                }
            }
            // 类型检查
            if (Array.isArray(val)) {
                return false
            }
            // 加入已选并勾选框
            this.subList.push(val)
            this.$refs.dd.toggleRowSelection(val, true)
        },
        // 去除已选
        popSelected (val) {
            this.subList.forEach((item, index) => {
                if (item.inspectionCode == val.inspectionCode) {
                    this.subList.splice(index, 1)
                    this.$refs.dd.toggleRowSelection(val, false)
                }
            })
        },
        selectAll (val) {
            if (val.length == 0) {
                this.subList = []
                this.$refs.dd.clearSelection()
                return false
            }
            val.forEach((items, indexv) => {
                let isSelected = false
                this.subList.forEach(item => {
                    if (item.inspectionCode === items.inspectionCode) {
                        isSelected = true
                    }
                })
                if (!isSelected) {
                    this.subList.push(val[indexv])
                }
            })
        },
        getList () {
            api.queryRecords(this.listQuery).then(res => {
                this.list = res.data.list
                this.total = res.data.pages.count
                // 已选中的勾选状态
                this.subList.forEach(item => {
                    for (let i = 0; i < this.list.length; i++) {
                        if (item.inspectionCode == this.list[i].inspectionCode) {
                            this.$nextTick(() => {
                                this.$refs.dd.toggleRowSelection(
                                    this.list[i],
                                    true
                                )
                            })
                            break
                        }
                    }
                })
            })

        },
    }
};
