import *  as api from '@/api/project/eas/er/equipmentMaintenance'
import { mapState } from "vuex"
import Pagination from '@/components/Pagination'


export default {
    name: 'dispatchOrderManagement',
    components: { Pagination },
    props: ['workOrderNumber', 'selectlist'],
    data () {
        return {
            list: null,
            total: 0,
            edList: [],
            subList: [],
            ids: [],
            materielRow: [],
            warehouse: undefined,
            theight: 0, //表格高度
            dialogStatus: '',
            dialogFormVisible: false,
            materielDialogVisible: false,
            listQuery: {
                page: true,
                currentPage: 1,
                pageSize: 15,
                workOrderNumber: undefined
            },
            tableKey: [0],
        }
    },
    created () {
    },
    watch: {
        dt_maintain_sp_state () {
            if (this.list && this.list.length) {
                let options = [this.dt_maintain_sp_state]
                this.list = this.$myFun.srmCodeToName(
                    this.list,
                    options,
                    ['state']
                )
            }

        }
    },
    computed: {
        ...mapState({
            dt_maintain_sp_state: state => state.eamDict.dt_maintain_sp_state,
            dt_unit_type: state => state.eamDict.dt_unit_type,
        })
    },
    mounted () {
        this.setTableHeight()
        this.$store.dispatch("eamDict/getDicData", [
            "dt_maintain_sp_state",
            "dt_unit_type"
        ])
        //表格高度自适应
        window.onresize = () => {
            this.setTableHeight()
        }
    },
    methods: {

        getList () {
            this.listQuery.workOrderNumber = this.workOrderNumber
            api.spRecords(this.listQuery).then(res => {
                this.list = res.data.list
                let options = [this.dt_maintain_sp_state]
                this.list = this.$myFun.srmCodeToName(
                    this.list,
                    options,
                    ['state']
                )
                this.total = res.data.pages.count
            })
        },
        // 获取表格选中时的数据
        selectRow (val) {
            this.selectlistRow = val
        },


        //表格高度计算
        setTableHeight () {
            this.theight = this.$myFun.getSingleTbHeight()
        },
    }
}
