import { getAlarm } from "@/api/project/iot/IOT/ga"
import Pagination from "@/components/Pagination"
import throttle from 'lodash/throttle'
import { queryGateways as queryAateway } from "@/api/project/iot/IOT/ga"
export default {
    name: "GAInfo",
    components: { Pagination },
    props: ['projectList', 'code', 'row'],
    watch: {
        'listQuery.projectName' (val) {
            if (!val) {
                this.listQuery.projectCode = undefined
            }
            else {
                this.projectList.forEach(item => {
                    if (item.projectName == val) {
                        return this.listQuery.projectCode = item.projectCode
                    }
                })
            }
        },
        'listQuery.projectCode' (val) {
            if (!val) {
                this.listQuery.gatewayName = undefined
            } else {
                queryAateway(val).then(res => {
                    this.gatewayList = res.data
                })
            }
        },
        'listQuery.gatewayName' (val) {
            if (!val) {
                this.listQuery.gatewayCode = undefined
            }
        },
    },
    data () {
        return {
            theight: 0,//表格高度
            listLoading: false,
            list: [],
            gatewayList: [],
            total: 0,
            listQuery: {
                page: true,
                currentPage: 1,
                pageSize: 15,
                gatewayCode: this.code,
                gatewayName: this.row.gatewayName,
                projectCode: this.row.projectCode,
                projectName: this.row.projectName,
                alarmTypes: [this.row.alarmType]//报警类型集合 this.row.alarmType
            },
            alarmTypes: [
                {
                    value: 'cpu',
                    label: '设备cpu占用率'
                },
                {
                    value: 'memUsed',
                    label: '设备内存使用率'
                },
                {
                    value: 'diskUsed',
                    label: '设备磁盘使用率'
                },
                {
                    value: 'yearFlow',
                    label: '年度设备通信流量统计'
                },
                {
                    value: 'signal',
                    label: '设备信号强度'
                },
                {
                    value: 'connected',
                    label: '网关上线'
                },
                {
                    value: 'disconnected',
                    label: '网关下线'
                }
            ],
            tableKey: 0 //表格索引
        }
    },
    mounted () {
        this.setTableHeight()
        window.onresize = () => {
            this.setTableHeight()
        }
        this.getList()
        this.getGateway()
    },
    methods: {
        getGateway () {
            this.projectList.forEach(item => {
                if (item.projectName == this.listQuery.projectName) {
                    return this.listQuery.projectCode = item.projectCode
                }
            })
            queryAateway(this.listQuery.projectCode).then(res => {
                this.gatewayList = res.data
            })
        },
        //获取表格数据
        getList () {
            this.listLoading = true
            getAlarm(this.listQuery).then(res => {
                this.list = res.data.list
                this.total = res.data.pages.count
                setTimeout(() => {
                    this.listLoading = false
                }, 1.5 * 100)
            })
        },
        //表格查询
        handleQuery: throttle(
            function () {
                this.listQuery.currentPage = 1
                this.getList()
            },
            2000,
            {
                leading: true,
                trailing: false
            }
        ),
        toInfo (row) {
            this.$emit('setCode', row)
            this.$emit('setPage', 'GA-Info')
        },
        //表格高度计算
        setTableHeight () {
            this.theight = this.$myFun.getSingleTbHeight() - 30
        },
        querySearchProject (queryString, cb) {
            var restaurants = this.projectList
            var results = queryString ? restaurants.filter(this.createFilterProject(queryString)) : restaurants
            cb(results)
        },
        createFilterProject (queryString) {
            return (restaurant) => {
                return (restaurant.projectName.toLowerCase().indexOf(queryString.toLowerCase()) !== -1)
            }
        },
        handleSelectProject (item) {
            this.listQuery.projectCode = item ? item.projectCode : undefined
            this.listQuery.projectName = item ? item.projectName : undefined
        },
        querySearchGateway (queryString, cb) {
            var restaurants = JSON.parse(JSON.stringify(this.gatewayList))
            var results = queryString ? restaurants.filter(this.createFilterGateway(queryString)) : restaurants
            cb(results)
        },
        createFilterGateway (queryString) {
            return (restaurant) => {
                return (restaurant.gatewayName.toLowerCase().indexOf(queryString.toLowerCase()) !== -1)
            }
        },
        handleSelectGateway (item) {
            this.listQuery.gatewayCode = item ? item.gatewayCode : undefined
            this.listQuery.gatewayName = item ? item.gatewayName : undefined
        }
    }
};
