import * as api from '@/api/project/eas/database/supplier'
import Pagination from '@/components/Pagination'
import { mapState } from 'vuex'

export default {
    name: 'gyszsj',
    components: { Pagination },
    data () {
        return {
            list: null,
            total: 0,
            theight: 0, //表格高度
            listQuery: {
                page: true,
                currentPage: 1,
                pageSize: 15,
                manufacturersCode: undefined,
                manufacturersName: undefined,
                manufacturersType: undefined
            },
            temp: {
                manufacturersCode: undefined,
                manufacturersName: undefined,
                manufacturersType: undefined,
                supplyClassify: undefined,
                address: undefined,
                contact: undefined,
                contactNumber: undefined,
                email: undefined,
                remark: undefined
            },
            manufacturersCode: undefined,
            selectCode: [],
            dialogFormVisible: false,
            dialogStatus: '',
            tableKey: 0,
            importDialogVisible: false,

        }
    },
    computed: {
        ...mapState({
            dt_manufacturers_type: state => state.dict.dt_manufacturers_type,

        })
    },
    mounted () {
        this.$store.dispatch('dict/getDicData', [
            'dt_manufacturers_type'
        ])
        this.setTableHeight()
        //表格高度自适应
        window.onresize = () => {
            this.setTableHeight()
        }
        this.getList()
    },
    methods: {
        getList () {
            api.queryRecords(this.listQuery).then(res => {
                this.list = res.data.list
                this.total = res.data.pages.count
            })
        },

        //表格高度计算
        setTableHeight () {
            this.theight = this.$myFun.getSingleTbHeight()
        },
        handleQuery () {
            this.listQuery.currentPage = 1
            this.getList()
        },
        selectRow (val) {
            this.selectlistRow = val[val.length - 1]
            if (val.length > 1) {
                this.$refs.tb_a.clearSelection() //清除其他行的选中
                this.$refs.tb_a.toggleRowSelection(val[val.length - 1], "selected")
            }
            this.$emit("change", this.selectlistRow)

        },

    }
}
