// import * as api from "@/api/purchase/delivery_Publish";
import dialogsbqd from "./dialog/dialogsbqd"
import * as api from "@/api/project/srm/supplierManagement/supplierRegister"
import { mapState } from "vuex"
export default {
    name: "delivery_Publish",
    components: { dialogsbqd },
    data () {
        return {
            theight: 0,//表格高度
            isSingle: true,//表格是否单选 点击各按钮根据流程逻辑控制单多选
            currentSelectedRow: null,//当前选中的行
            selectedRows: null,//多选时选中的所有行
            list: null,
            listSub: null,
            total: 0,
            selectedrow: null,//主表选中行
            tableKey: 0//表格索引，虚拟dom渲染表格时使用
        }
    },
    computed: {
        ...mapState({
            dt_srm_paa: state => state.dict.dt_srm_paa,
        })
    },
    mounted () {
        this.$store.dispatch("dict/getDicData", ["dt_srm_paa"])
        this.setTableHeight()
        //表格高度自适应
        window.onresize = () => {
            this.setTableHeight()
        }
    },
    methods: {
        setTabData (val) {
            this.list = val
        },
        //表格高度计算
        setTableHeight () {
            this.theight = this.$myFun.getSingleTbHeight() - 185
        },
        //新增编辑删除成功之后刷新表格数据
        getFreshList () {
            let b = {
                rowId: this.rowId
            }
            api.updateRecords(b).then(res => {
                let options = [this.dt_srm_paa]
                res.data.equipmentInfoDTOList = this.$myFun.srmCodeToName(res.data.equipmentInfoDTOList, options, ['category'])
                this.list = res.data.equipmentInfoDTOList
            })
        },
        //显示主表
        getList (row) {
            this.$refs.sbqd.temp.supplierId = row.rowId
            this.rowId = row.rowId
            let a = {
                rowId: row.rowId
            }
            api.updateRecords(a).then(res => {
                let options = [this.dt_srm_paa]
                res.data.equipmentInfoDTOList = this.$myFun.srmCodeToName(res.data.equipmentInfoDTOList, options, ['category'])
                this.list = res.data.equipmentInfoDTOList
            })
        },
        //新增按钮单击事件
        handleCreate () {
            this.$refs.sbqd.resetTemp()
            this.$refs.sbqd.dialogStatus = 'create'
            this.$refs.sbqd.dialogVisible = true
        },
        //行内编辑按钮单击事件
        handleUpdate (index, val) {
            this.$refs.sbqd.dialogStatus = 'update'
            this.$refs.sbqd.dialogVisible = true
            this.$refs.sbqd.temp = val
        },
        //删除
        handleDelete (index, val) {
            api.deleteRecordsbqd(this.$refs.sbqd.temp.supplierId, val.rowId).then(() => {
                this.$message({
                    title: '成功',
                    message: '删除成功',
                    type: 'success',
                    duration: 2000
                })
                this.getFreshList()
            })
        },
        // 表格选择框选中 注:参数为选中的所有行的数组
        selectChange (val) {
            if (this.isSingle) {
                if (val.length > 1) {
                    this.$refs.tb.clearSelection() //清除其他行的选中
                    this.$refs.tb.toggleRowSelection(val[val.length - 1], "selected") //单击行绑定点击事件
                } else if (val.length === 1) {
                    this.selectedRows = val
                    this.currentSelectedRow = val[val.length - 1]
                }
            } else {
                this.selectedRows = val
                this.currentSelectedRow = val[val.length - 1]
            }
        },
        //点击表格某一行
        rowClick (val) {
            if (this.isSingle) {
                this.$refs.tb.clearSelection() //清除其他行的选中
            }
            this.$refs.tb.toggleRowSelection(val, "selected") //单击行绑定点击事件
        },
        //加载时默认选中第一行
        setFirstLine () {
            this.$nextTick(() => {
                this.$refs.tbmain.setCurrentRow(this.list[0])
            })
        }
    }
};
