export default {
    name: "easLine",
    props: ['id', 'data'],
    data () {
        return {
            mycharts: undefined,
        }
    },
    watch: {
        data () {
            this.getLine()
        }
    },
    mounted () {
        let _this = this
        window.addEventListener('resize', function () {
            if (_this.mycharts) {
                _this.mycharts.resize()
            }
        })
        // this.getLine()
    },
    methods: {
        getLine () {
            console.log(this.data)
            if (!this.data) return false
            this.myChart = this.$echarts.init(document.getElementById(this.id))
            let option = {
                grid: {   // 直角坐标系内绘图网格
                    left: '10',
                    top: '10',
                    bottom: '10',
                    containLabel: true
                },
                tooltip: {
                    trigger: 'axis',
                    axisPointer: {            // 坐标轴指示器，坐标轴触发有效
                        type: 'shadow'        // 默认为直线，可选为：'line' | 'shadow'
                    }
                },
                xAxis: {
                    type: 'category',
                    boundaryGap: false,
                    data: this.data.xAxisData
                },
                yAxis: {
                    type: 'value'
                },
                series: [
                    {
                        data: this.data.data1,
                        type: 'line',
                        itemStyle: {
                            normal: {
                                color: 'yellow',
                                borderColor: '#fff',
                                borderWidth: 1,
                                borderType: 'solid',
                                shadowColor: '#fff',
                                shadowBlur: 4,
                            },
                        },
                        lineStyle: {
                            normal: {
                                color: '#1a7ba1',//折线颜色
                            }
                        },
                        areaStyle: {
                            normal: {
                                color: {
                                    type: 'linear',//设置线性渐变
                                    x: 0,
                                    y: 0,
                                    x2: 0,
                                    y2: 1,
                                    colorStops: [{
                                        offset: 0, color: '#05b4f7' // 0% 处的颜色
                                    }, {
                                        offset: 1, color: '#05b4f7' // 100% 处的颜色
                                    }],
                                    globalCoord: false // 缺省为 false
                                },
                            }
                        },
                        showSymbol: false
                    },
                    {
                        data: this.data.data2,
                        type: 'line',
                        showSymbol: false,
                        lineStyle: {
                            color: 'rgb(24, 204, 120)',//折线颜色
                        },
                    }
                ]
            }
            this.myChart.setOption(option, true)
        }
    }
};
