import * as api from "@/api/project/srm/contractManage/specialPaymentMethod"
import Pagination from '@/components/Pagination'
import throttle from 'lodash/throttle'

export default {
    components: { Pagination },
    data () {
        return {
            dialogVisible: false,
            listQuery: {
                page: true,
                currentPage: 1,
                pageSize: 15,
            },
            isSingle: false,
            tableKey: 0,
            dataList: [],
            total: 0,
            rowId: null
        }
    },
    mounted () {
        // this.getList()
    },
    filters: {
        statusFilter: function (a) {
            if (a === 'true') {
                return "是"
            } else if (a == 'false') {
                return "否"
            }
        },
    },
    methods: {
        getList () {

        },
        handleQuery () {
            this.getList()
        },
        confirm: throttle(function () {
            if (this.selectlistRow && this.selectlistRow.length > 0) {
                let dto = {
                    method: 'approval',
                    rowIds: []
                }
                this.selectlistRow.forEach((val, index) => {
                    dto.rowIds[index] = val.rowId
                })
                api.updateEffect(dto).then(() => {
                    this.$message({
                        title: '成功',
                        message: '生成审批单成功',
                        type: 'success',
                        duration: 2000
                    })
                    this.dialogVisible = false
                    this.$emit('refresh')
                })
            } else {
                this.$message({
                    title: '警告',
                    message: '请勾选记录',
                    type: 'warning',
                    duration: 2000
                })
            }
        }, 2000,
            {
                leading: true,
                trailing: false
            }
        ),
        // 表格选择框选中 注:参数为选中的所有行的数组
        selectChange (val) {
            this.selectlistRow = val
        },
        //点击表格某一行
        rowClick (val) {
            this.$refs.tb.toggleRowSelection(val)
        }
    }
}
