import * as api from "@/api/project/srm/priceManage/purchasePriceInquiry"
import Pagination from "@/components/Pagination"
import { DICT_CODE } from '@/utils/constant'
import caiGou from '@/components/Select/xtpt/caigou'//下拉组件
import { mapState } from "vuex"
import ImportFile from "./importFile"
export default {
    name: "purchasePriceInquiry",
    components: { Pagination, caiGou, ImportFile },
    data () {
        return {
            rangeDate: null,
            Edit: true,
            DICT_CODE: DICT_CODE,
            theight: 0,//表格高度
            isSingle: true,//表格是否单选 点击各按钮根据流程逻辑控制单多选
            list: [],
            total: 0,
            radio: 1,
            listQuery: {
                //查询
                page: true,
                currentPage: 1,
                pageSize: 15
            },
            currentSelectedRow: null,//当前选中的行
            selectedRows: null,//多选时选中的所有行
            tableKey: 0, //表格索引
            importDialogVisible: false,
            modalNo: 'EXTP-BAS-0025'  // 导入模板编号
        }
    },
    created () { },
    watch: {
        'rangeDate' (value) {
            if (value) {
                this.listQuery.validTimeStart = value[0]
                this.listQuery.validTimeEnd = value[1]
            } else {
                this.listQuery.validTimeStart = undefined
                this.listQuery.validTimeEnd = undefined
            }
        },
        'radio' (val) {
            if (val == 1) {
                this.Edit = true
                this.rangeDate = null
                this.listQuery.validTimeStart = undefined
                this.listQuery.validTimeEnd = undefined
            } else {
                this.Edit = false
            }
        }
    },
    mounted () {
        this.$store.dispatch("dict/getDicData", ["dt_srm_p0"])
        this.setTableHeight()
        //表格高度自适应
        window.onresize = () => {
            this.setTableHeight()
        }
        this.getList()
    },
    computed: {
        ...mapState({
            dt_srm_p0: state => state.dict.dt_srm_p0,//状态
        })
    },
    methods: {
        //表格高度计算
        setTableHeight () {
            this.theight = this.$myFun.getSingleTbHeight()
        },
        //获取表格数据
        getList () {
            if (this.radio == 1) {
                this.listQuery.curValidFlag = this.radio
            } else {
                delete this.listQuery.curValidFlag
            }

            api.queryRecords(this.listQuery).then(res => {
                console.log(res, 'kkkk')
                let options = [this.dt_srm_p0]
                res.data.list = this.$myFun.codeToName(res.data.list, options, ['status'])
                this.list = res.data.list
                this.total = res.data.pages.count
            })
        },
        //表格查询
        handleQuery () {
            this.listQuery.currentPage = 1
            this.getList()
        },
        // 导入
        handleImport() {
            this.importDialogVisible = true
            this.$nextTick(() => {
                this.$refs.importDialog.listQuery = this.listQuery
                this.$refs.importDialog.excelName = '采购价格.xlsx'
                this.$refs.importDialog.getList()
            })
        },
        // 导入弹框右上角关闭
        handleClose () {
            this.importDialogVisible = false
            // 清空已选文件列表
            this.$refs.importDialog.fileList = []
            this.getList()
        },
        // 导出
        handleExport () {
            this.downloadLoading = true
            import('@/vendor/Export2Excel').then(excel => {
                const tHeader = ['序号', '采购组织', '物料编码', '物料名称', '供应商编码', '供应商名称', '价格单位', '采购计量单位', '税率', '不含税价', '含税价', '基本计量单位', '转换比', '有效开始', '有效结束']
                const filterVal = ['rowId', 'orgName', 'materielCode', 'materielName', 'supplierCode', 'supplierName', 'priceUnit', 'measureUnit', 'taxRate', 'taxFreePrice', 'taxPrice', 'baseUnit', 'conversionRate', 'validTimeStart', 'validTimeEnd']
                const data = this.formatJson(filterVal, this.list)
                excel.export_json_to_excel({
                    header: tHeader,
                    data,
                    filename: '采购价格导出'
                })
                this.downloadLoading = true
            })
        },
        formatJson (filterVal, jsonData) {
            return jsonData.map(v => filterVal.map(j => {
                if (j === 'timestamp') {
                    return
                } else {
                    return v[j]
                }
            }))
        },
    }
};
