            this.$refs.addMain.dialogVisible = true
        },
        //增版按钮单击事件
        handleAdd () {
            this.$refs.addVersion.dialogVisible = true
        },
        //新增供应商选择
        handleCreateGYS () {
            this.$nextTick(() => {
                this.$refs.refGys.getList()
                // this.$refs.refGys.initSelect()
            })
            this.$refs.refGys.valueType = 'row'
            this.$refs.refGys.dialogVisible = true
        },
        //供应商选择确定
        handleGysClick (val) {
            console.log(val)
