                    //     })
                    //     this.getList()
                    // })
                }).catch(() => {
                    this.$message({
                        type: "info",
                        message: "已取消"
                    })
                })
            } else {
                this.$message({
                    title: "警告",
                    message: "请选择一条信息",
                    type: "warning"
                })
            }
        },
        //传按钮单击事件
        handlePass () {

        },
        //下按钮单击事件
        handleDownLoad () {

        },
        //明细删除按钮单击事件
        handleDeleteItems () {
            if (this.selectlistRowItem) {
                this.$confirm("是否删除该条信息？", "提示", {
                    confirmButtonText: "确定",
                    cancelButtonText: "取消",
                    type: "warning"
                }).then(() => {
                    // api.deleteRecord(val.rowId).then(() => {
