                    //         title: '成功',
                    //         message: '生效成功',
                    //         type: 'success',
                    //         duration: 2000
                    //     })
                    //     this.getList()
                    // })
                }).catch(() => {
                    this.$message({
                        type: "info",
                        message: "已取消"
                    })
                })
            } else {
                this.$message({
                    title: "警告",
                    message: "请选择一条信息",
                    type: "warning"
                })
            }
        },
        //发布按钮单击事件
        handlePublish () {

        },
        //撤回按钮单击事件
        handleWithdraw () {

        },
        //查询
        handleQuery () {
            this.listSub = []
            this.listQuery.currentPage = 1
            this.getList()
        },
        // 表格选择框选中 注:参数为选中的所有行的数组
        selectChange (val) {
            this.selectlistRow = val[val.length - 1]
            if (val.length > 1) {
                this.$refs.tb.clearSelection()
                this.$refs.tb.toggleRowSelection(val.pop())
            }
            if (this.selectlistRow) {
                this.getSubList()
            }
            this.$refs.tb.setCurrentRow(this.selectlistRow)
        },
        //点击表格某一行
        rowClick (val) {
            this.$refs.tb.clearSelection()
            this.$refs.tb.toggleRowSelection(val)
        },
        // 表格选择框选中 注:参数为选中的所有行的数组
        selectChangeSub (val) {
            this.selectlistRowSub = val[val.length - 1]
            if (val.length > 1) {
                this.$refs.tbmain.clearSelection()
                this.$refs.tbmain.toggleRowSelection(val.pop())
            }
            this.$refs.tbmain.setCurrentRow(this.selectlistRowSub)
        },
        //点击表格某一行
        rowClickSub (val) {
            this.$refs.tbmain.clearSelection()
            this.$refs.tbmain.toggleRowSelection(val)
        },
