import * as api from '@/api/project/wms/purchaseStorage/qaJudge'
import caiGou from '@/components/Select/caigou'//下拉组件
import warehouseSelect from '@/components/Select/warehouse-select'//下拉组件

import Pagination from "@/components/Pagination";
import { DT_ORG_TYPE } from '@/utils/constant'

export default {
    name: "storageCheck",
    components: { Pagination, caiGou, warehouseSelect },
    data () {
        return {
            theight: 0,//表格高度
            isSingle: true,//表格是否单选 点击各按钮根据流程逻辑控制单多选
            list_a: [],
            total_a: 0,
            areaList: null,
            locatorList: [],
            listQuery_a: {
                currentPage: 1,
                pageSize: 15,
                state: 'jqc2'
            },
            list_a_1: [],
            total_a_1: 0,
            listQuery_a_1: {
                currentPage: 1,
                pageSize: 15,
            },
            dto: {},
            DT_ORG_TYPE: DT_ORG_TYPE,
            dialogFormVisible: false,
            temp: {},
            rules: {
                businessType: [
                    { required: true, message: this.$t('validate.required'), trigger: 'blur' },
                ],
            }
        };
    },
    mounted () {
        this.$store.dispatch('dict/getDicData', ['dt_band_type']);
        this.setTableHeight();
        this.getList_a();
        //表格高度自适应
        window.onresize = () => {
            this.setTableHeight()
        };
    },
    methods: {
        //表格高度计算
        setTableHeight () {
            this.theight = this.$myFun.getSingleTbHeight();
        },
        getList_a () {//获取质检判定单主表
            api.getData_a(this.listQuery_a).then(res => {
                res = res.data;
                this.list_a = res.list
                this.total_a = res.pages.count
            })
        },
        getList_a_1 () {//获取质检判定单从表
            this.listQuery_a_1.jqcOrderNumber = this.selectlistRow_a.jqcOrderNumber || this.selectlistRow_a.jqcOrderNumber
            api.getData_a_1(this.listQuery_a_1).then(res => {
                res = res.data;
                this.list_a_1 = res.list.map(v => {
                    for (const key in this.selectlistRow_a) {
                        if (!v.hasOwnProperty(key)) {
                            v[key] = this.selectlistRow_a[key]
                        }
                    }
                    return v
                })
                this.total_a_1 = res.pages.count
            })
            api.getArea(this.selectlistRow_a.warehouseCode).then(resa => {
                resa = resa.data;
                this.areaList = resa;
            });
        },
        getLocatorList (warehouseCode, areaCode, i) {
            api.getLocator(warehouseCode, areaCode).then(res => {
                res = res.data;
                this.$set(this.locatorList, i, res);
                this.$set(this.list_a_1[i], 'nextWareLocationCode', res[0].locationCode)
                this.$set(this.list_a_1, i, this.list_a_1[i])
            });
        },
        changeAreaSel (areaCode, i) {
            this.getLocatorList(this.selectlistRow_a.warehouseCode, areaCode, i);
        },
        selectRow_a (val) {//质检判定单主表选中事件
            this.selectlistRow_a = val[val.length - 1]
            if (val.length > 1) {
                this.$refs.dt_a.clearSelection()
                this.$refs.dt_a.toggleRowSelection(val.pop())
            }
            if (this.selectlistRow_a) {
                this.getList_a_1()
            } else {
                this.list_a_1 = []
                this.total_a_1 = 0
            }
        },
        selectRow_a_1 (val) {//质检判定从表主表选中事件
            this.selectListRow_a_1 = val
        },
        clickRow_a (val) {//质检判定主表点击事件
            this.selectlistRow_a = val
            this.$refs.dt_a.clearSelection()//清除其他行的选中
            this.$refs.dt_a.toggleRowSelection(val)//单击行绑定点击事件
        },
        confirm () {
            let result = true
            this.list_a_1.forEach(val => {
                if (val.retentionQuantity >= 1 && !val.nextWareAreaCode && !val.nextWareLocationCode) {
                    this.$message({
                        title: this.$t("message.warning"),
                        message: "请先选择区位和储位",
                        type: "warning"
                    });
                    result = false
                }
            })
            if (result) {
                return this.list_a_1
            } else {
                return false
            }
        },
        qualityConfirmation () {//质检确认
            let a = this.confirm()
            this.dto = {
                jqcOrderNumber: this.selectlistRow_a.jqcOrderNumber,
                method: 'CONFIRM',
                judgingQcDTO: {
                    judgingQcItems: a
                }
            }
            if (a) {
                api.update_a(this.dto).then(() => {
                    this.$message({
                        title: "成功",
                        message: "质检确认成功",
                        type: "success"
                    });
                    this.getList_a()
                })
            }

        },
        handleRecommend () {
            if (this.selectlistRow_a) {
                if (this.selectListRow_a_1) {
                    this.dto.businessType = this.selectlistRow_a.businessType;
                    this.dto.orgCode = this.selectlistRow_a.orgCode;
                    this.dto.wareMaterielDTOS = this.selectListRow_a_1.map(v => {
                        let DTOSlist = {}
                        DTOSlist.materielCode = v.materielCode
                        DTOSlist.materielClassify = ""
                        return DTOSlist
                    });
                    this.dto.warehouseCode = this.selectlistRow_a.warehouseCode,
                        api.recommend(this.dto).then(res => {
                            res = res.data;
                            res.forEach((val) => {
                                this.list_a_1.forEach((val1, index) => {
                                    if (val.materielCode == val1.materielCode) {
                                        this.$set(this.list_a_1[index], 'nextWareAreaCode', val.areaCode);
                                        this.$set(this.list_a_1[index], 'nextWareLocationCode', val.locationCode);
                                        this.$set(this.list_a_1, index, this.list_a_1[index]);
                                    }
                                })
                            })
                        })
                } else {
                    this.$message({
                        title: "警告",
                        message: "请选择明细信息",
                        type: "warning"
                    })
                }

            } else {
                this.$message({
                    title: "警告",
                    message: "请选择一条信息",
                    type: "warning"
                })
            }
        }
    }
};
