// import search from '../dialog/search' //评价类型明细
export default {
    // components: { search },
    data () {
        return {
            dialogVisible: false,
            list: [],
            theight: 200,
            isSingle: true,
            selectedRow: null,
            // 当前选中的
            currentSelectedRow: null,
            tableData: [
                { label: '评价单号', prop: 'evaluationNo', align: 'center', width: 'auto' },
                { label: '行号', prop: 'num', align: 'center', width: 'auto' },
                { label: '评价项', prop: 'evaluationItem', align: 'center', width: 'auto' },
                { label: '评价分数', prop: 'score', align: 'center', width: 'auto' },
                { label: '评价等级', prop: 'grade', align: 'center', width: 'auto' },
                { label: '推荐意见', prop: 'recommendResult', align: 'center', width: 'auto' },
                { label: '评价结论', prop: 'evaluationConclusion', align: 'center', width: 'auto' },
                { label: '状态', prop: 'state', align: 'center', width: 'auto' },
                { label: '评价人', prop: 'evaluationPerson', align: 'center', width: 'auto' },
                { label: '考察人', prop: 'inspector', align: 'center', width: 'auto' },
            ],
            form: {
                score: undefined,
                grade: undefined,
                recommendResult: undefined,
                remark: undefined
            }
        }
    },
    mounted () {
        // this.setTableHeight()
        // //表格高度自适应
        // window.onresize = () => {
        //     this.setTableHeight()
        // }
        // this.getList()
    },
    methods: {
        // 初始化表单
        initForm () {
            this.form = {
                recommendResult: undefined,
                remark: undefined
            }
        },
        // 查
        // handleView (index, val) {
        //     this.$refs.search.dialogStatus = 'see'
        //     this.$refs.search.dialogVisible = true
        //     this.$refs.search.temp = { ...val }
        // },
        // 综合评价确定
        handleConfirm () {
            this.$emit('handleConfirm', this.form)
        },
        // 多选框
        selectChange (val) {
            if (this.isSingle) {
                this.selectedRow = val[val.length - 1]
                if (val.length == 1) {
                    // 1. 选中的只有一个时
                    // 当前行高亮
                    this.$refs.tb.setCurrentRow(this.selectedRow)
                } else if (val.length > 1) {
                    // 2. 当前有多个选中时
                    // 2.1 清除其他行的选中
                    this.$refs.tb.clearSelection()
                    // 2.2 切换选中状态
                    this.$refs.tb.toggleRowSelection(this.selectedRow, 'selected')

                    // 2.3 当前选中行高亮
                    this.$refs.tb.setCurrentRow(this.selectedRow)
                } else {
                    // 3. 无选中（也就是把当前选中的取消）
                    this.$refs.tb.setCurrentRow()
                }
            } else {
                this.selectedRow = val
                this.currentSelectedRow = val[val.length - 1]
            }
        },
        // 点击某行
        rowClick (val) {
            this.selectedRow = val
            // 清除其它行的选中
            this.$refs.tb.clearSelection()
            // 切换成选中状态
            this.$refs.tb.toggleRowSelection(this.selectedRow, true)
        },
    }
}
