import * as api from '@/api/project/ems/onlineMonitoring/online.js'
export default {
    name: 'productionMonitoring', //产线监控与预警
    data () {
        return {
            theight: 0, //表格高度
            list: [],
            listQuery: {
                warningName: 'productLineWarning'
            },
            remark: [
                { class: 'fault', info: '水' },
                { class: 'standby', info: '电' },
                { class: 'run', info: '气' }
            ],
            isMore: false,
            loading: true,
            setTimeoutValue: null
        }
    },
    mounted () {
        this.setTableHeight()
        //表格高度自适应
        window.onresize = () => {
            this.setTableHeight()
        }
        this.getList()
    },
    methods: {
        //表格高度计算
        setTableHeight () {
            this.theight = this.$myFun.getSingleTbHeight()
        },
        showMore () {
            this.isMore = !this.isMore
        },
        //获取表格数据
        getList () {
            api.query(this.listQuery).then(response => {
                this.list = response.data
                this.loading = false
            })
        },
        setTimeout () {
            this.setTimeoutValue = setInterval(() => {
                this.getList()
            }, 60000 * 5)
        },
        clear () {
            if (this.setTimeoutValue) {
                clearInterval(this.setTimeoutValue)
            }
        }
    },
    computed: {
        holder () {
            return this.list.length <= 6
                ? 6 - this.list.length
                : 6 - (this.list.length % 6)
        }
    },
    destroyed () {
        this.setTableHeight()
        this.clear()
    }
}
