import * as api from "@/api/project/srm/supplierSelect/technicalReview"
import { mapState } from "vuex"
export default {
    data () {
        return {
            list: [],
            dialogVisible: false,
            temp: {}
        }
    },
    computed: {
        ...mapState({
            dt_srm_pag: state => state.dict.dt_srm_pag,
        })
    },
    mounted () {
        this.$store.dispatch("dict/getDicData", ['dt_srm_pag'])
    },
    methods: {
        handleDelete (index, val) {
            this.$confirm("确认要删除该数据项吗?", "提示", {
                confirmButtonText: "确定",
                cancelButtonText: "取消",
                type: "warning"
            }).then(() => {
                this.list = this.list.filter(item => {
                    let isD = true
                    if (val.rowId === item.rowId) {
                        isD = false
                    }
                    return isD
                })
                this.$message({
                    type: "success",
                    message: "删除成功"
                })
            }).catch(() => {
                this.$message({
                    type: "info",
                    message: "已取消"
                })
            })
        },
        confirm () {
            let dto = {
                evaluationConclusion: this.temp.evaluationConclusion,
                evaluationProblem: this.temp.evaluationProblem,
                failureReason: this.temp.failureReason,
                rectificationOpinion: this.temp.rectificationOpinion,
                rfqNo: this.temp.rfqNo,
                rowIds: []
            }
            this.list.forEach((val, index) => {
                dto.rowIds[index] = val.rowId
            })
            console.log(dto, '1111')
            api.updateRecord(dto).then(() => {
                this.$message({
                    title: '成功',
                    message: '评审成功',
                    type: 'success',
                    duration: 2000
                })
                this.dialogVisible = false
                this.$emit('getFreshList')
            })
        },
        resetTemp () {
            this.temp = {
                evaluationConclusion: undefined,
                evaluationProblem: undefined,
                failureReason: undefined,
                rectificationOpinion: undefined
            }
        }
    }
}
